color_dark_bronze = [120/255, 80/255, 40/255];
color_gold = [200/255, 160/255, 50/255];

module astrolabe_compendium() {
    // Main body - circular base
    color(color_bronze)
    difference() {
        cylinder(h = thickness, d = main_diameter, center = true);

        // Hollow center
        translate([0, 0, -1])
        cylinder(h = thickness + 2, d = main_diameter - 2*wall_thickness, center = true);
    }

    // Suspension ring mount
    translate([0, main_diameter/2 + 5, thickness/2 - 2])
    rotate([90, 0, 0])
    color(color_dark_bronze)
    difference() {
        cylinder(h = 8, d = 10, center = true);
        cylinder(h = 10, d = 5, center = true);
    }

    // Suspension ring
    translate([0, main_diameter/2 + 5, thickness/2 - 2])
    rotate([90, 0, 0])
    color(color_gold)
    rotate([0, 90, 0])
    torus(4, 1.5);

    // Rete (star map) - simplified
    translate([0, 0, thickness/2 + 1])
    color(color_gold)
    linear_extrude(height = 0.5)
    union() {
        // Main star pattern
        circle(d = main_diameter - 20);

        // Ecliptic circle
        circle(d = main_diameter - 30);

        // Star pointers
        for(i = [0:30:359]) {
            rotate([0, 0, i])
            translate([main_diameter/2 - 15, 0, 0])
            circle(d = 2);
        }
    }

    // Rule (straight measuring arm)
    translate([0, 0, thickness/2 + 2])
    color(color_dark_bronze)
    rotate([0, 0, 45])
    cube([main_diameter - 10, 3, 1], center = true);

    // Alidade (sighting rule)
    translate([0, 0, -thickness/2 - 1])
    color(color_bronze)
    rotate([0, 0, 0])
    cube([main_diameter - 15, 5, 1], center = true);

    // Sighting vanes
    translate([main_diameter/2 - 10, 0, -thickness/2 - 1])
    rotate([0, 0, 0])
    color(color_gold)
    union() {
        // Left vane
        translate([-2.5, -5, 0])
        cube([5, 10, 2]);

        // Right vane
        translate([2.5, -5, 0])
        cube([5, 10, 2]);
    }

    // Engraved degree scale
    translate([0, 0, thickness/2 - 0.1])
    color(color_dark_bronze)
    linear_extrude(height = 0.2)
    difference() {
        circle(d = main_diameter - 4);

        // Degree markings
        for(i = [0:5:359]) {
            rotate([0, 0, i])
            translate([main_diameter/2 - 6, -0.5, 0])
            square([5, 1]);
        }
    }

    // Central pin
    translate([0, 0, 0])
    color(color_gold)
    union() {
        cylinder(h = thickness + 4, d = 4, center = true);
        sphere(d = 5);
    }

    // Decorative border
    translate([0, 0, thickness/2])
    color(color_gold)
    for(i = [0:15:359]) {
        rotate([0, 0, i])
        translate([main_diameter/2 - 3, 0, 0])
        sphere(d = 2);
    }
}

module torus(major, minor) {
    rotate_extrude()
    translate([major, 0, 0])
    circle(r = minor);
}

