// Medieval Illuminated Handbook (Book of Hours style)
// Dimensions: 60mm x 45mm x 15mm when closed

// Overall scale factor
scale_factor = 0.5;

// Book dimensions in mm
book_width = 60 * scale_factor;
book_height = 45 * scale_factor;
book_thickness = 15 * scale_factor;
cover_thickness = 2 * scale_factor;
page_thickness = 11 * scale_factor;
spine_width = 8 * scale_factor;

// Colors
cover_color = "saddlebrown";
clasp_color = "goldenrod";
page_color = "beige";
gem_color = "darkblue";

// Main book module
module medieval_handbook() {
    // Front cover
    color(cover_color) {
        // Main cover
        translate([-book_width/2, -book_height/2, -book_thickness/2]) 
            cube([book_width, book_height, cover_thickness]);

        // Spine
        translate([-book_width/2 - spine_width/2, -book_height/2, -book_thickness/2])
            cube([spine_width, book_height, book_thickness]);

        // Raised decorative border
        translate([-book_width/2 + 2, -book_height/2 + 2, -book_thickness/2 + cover_thickness])
            cube([book_width - 4, book_height - 4, 0.5]);

        // Corner decorations (4 corners)
        for(x = [-1, 1]) {
            for(y = [-1, 1]) {
                translate([x * (book_width/2 - 5), y * (book_height/2 - 5), 
                          -book_thickness/2 + cover_thickness + 0.5]) {
                    rotate([0, 0, 45 * (x * y)]) 
                        cube([3, 3, 0.5], center=true);
                }
            }
        }

        // Central boss/gem setting
        translate([0, 0, -book_thickness/2 + cover_thickness + 1]) {
            cylinder(h=1, r1=4, r2=4, $fn=16);
            color(gem_color) 
                translate([0, 0, 0.5]) 
                    cylinder(h=0.5, r1=3, r2=3, $fn=16);
        }
    }

    // Metal clasp
    color(clasp_color) {
        // Hinge side
        translate([book_width/2 - 1, -3, -book_thickness/2 + cover_thickness]) 
            cube([2, 6, page_thickness]);

        // Clasp loop
        translate([book_width/2 + 1, 0, -book_thickness/2 + book_thickness/2]) {
            difference() {
                cube([4, 8, 2]);
                translate([2, 4, -0.1]) 
                    cylinder(h=2.2, r1=2, r2=2, $fn=16);
            }
        }
    }

    // Pages
    color(page_color) {
        translate([-book_width/2, -book_height/2, 
                  -book_thickness/2 + cover_thickness]) 
            cube([book_width, book_height, page_thickness]);
    }

    // Page edges details
    color(page_color) {
        // Gilded page edges effect
        for(i = [0:5:page_thickness-2]) {
            translate([-book_width/2 - 0.1, -book_height/2, 
                      -book_thickness/2 + cover_thickness + i]) 
                cube([book_width + 0.2, book_height, 1]);
        }
    }

    // Spine ridges (raised bands)
    color(cover_color) {
        for(i = [-1, 0, 1]) {
            translate([-book_width/2 - spine_width/2, 
                      i * (book_height/3), 
                      -book_thickness/2 + cover_thickness]) {
                cube([spine_width, 2, book_thickness - 2*cover_thickness]);
            }
        }
    }

    // Back cover
    color(cover_color) {
        translate([-book_width/2, -book_height/2, 
                  -book_thickness/2 + cover_thickness + page_thickness]) 
            cube([book_width, book_height, cover_thickness]);
    }
}

// Render the handbook
medieval_handbook();

// Manual Roman wax tablet (tabula cerata)  circa 100 CE
// Typical openformat writing tablet with two hinged leaves
// Overall footprint  75 mm  55 mm  12 mm closed

// ------------------- Parameters -------------------
th_leaf = 5;            // thickness of one wooden leaf
gap = 1;                // hinge clearance
th_wax = 1.5;           // wax layer depth
r_corner = 3;           // corner radius for printability
th_wall = 1.8;          // minimum wall thickness for FDM
pin_r = 1.5;            // hinge pin radius
pin_l = 2 * th_leaf + gap + 2;  // pin length

// ------------------- Colours ----------------------
color_wood   = [0.55, 0.35, 0.15];
color_wax    = [0.25, 0.20, 0.10];
color_metal  = [0.60, 0.60, 0.55];

// ------------------- Modules ----------------------
module rounded_box(size, r, center=false) {
    // center must be false so that translate() behaves predictably
    hull() {
        translate([r, r, 0]) cube([size.x-2*r, size.y-2*r, size.z], center=center);
        translate([r, size.y-r, 0]) sphere(r);
        translate([size.x-r, r, 0]) sphere(r);
        translate([size.x-r, size.y-r, 0]) sphere(r);
        translate([r, r, size.z]) sphere(r);
        translate([r, size.y-r, size.z]) sphere(r);
        translate([size.x-r, r, size.z]) sphere(r);
        translate([size.x-r, size.y-r, size.z]) sphere(r);
    }
}

module leaf() {
    // Wooden substrate
    color(color_wood) {
        difference() {
            rounded_box([75, 55, th_leaf], r_corner);
            // Recess for wax
            translate([5, 5, th_leaf - th_wax])
                cube([65, 45, th_wax + 0.1]);
        }
    }
    // Wax layer
    color(color_wax)
        translate([5, 5, th_leaf - th_wax])
            cube([65, 45, th_wax]);
}

module hinge_pin() {
    color(color_metal)
        cylinder(h = pin_l, r = pin_r, center = true);
}

module hinge_half(teeth, y0) {
    // one half of a simple barrel hinge
    tooth_w = 4;
    tooth_h = 4;
    for (i = [0:teeth-1])
        translate([0, y0 + i * (tooth_w + 1), 0])
            cube([tooth_h, tooth_w, th_leaf]);
}

// ------------------- Main assembly ----------------
translate([0, -55/2, 0]) {
    // bottom leaf
    leaf();

    // top leaf (rotates around hinge)
    translate([0, 0, th_leaf + gap])
        rotate([0, 0, 0])  // default closed
        leaf();
}

// hinge barrels
translate([75 - 2, 0, th_leaf + gap/2]) {
    // bottom barrels
    hinge_half(3, 5);

    // top barrels, offset one tooth
    translate([0, 0, th_leaf + gap])
        hinge_half(2, 5 + 4 + 1);

    // hinge pin
    translate([2, 5 + 2 + 1, 0])
        rotate([90, 0, 0])
            hinge_pin();
}

// Decorative bronze corner plates (reinforcements)
module corner_plate() {
    color(color_metal)
        cube([8, 8, 0.6]);
}

translate([0, 0, 0]) corner_plate();
translate([75-8, 0, 0]) mirror([1, 0, 0]) corner_plate();
translate([0, 55-8, 0]) mirror([0, 1, 0]) corner_plate();
translate([75-8, 55-8, 0]) mirror([1, 1, 0]) corner_plate();

