// Historical Roman Wax Tablet (Tabula) - 1st Century AD
// Realistic medium-complexity model for 3D printing

// Overall dimensions in mm
tablet_width = 80;
tablet_height = 50;
tablet_thickness = 8;
wood_thickness = 3;
wax_depth = 2;

// Hinge dimensions
hinge_diameter = 4;
hinge_length = 40;
hinge_gap = 2;

// Stylus dimensions
stylus_length = 70;
stylus_diameter = 3;

// Colors
wood_color = "#8B4513";
wax_color = "#DAA520";
bronze_color = "#CD7F32";

module wooden_frame() {
    // Main wooden frame
    color(wood_color) {
        // Outer frame
        difference() {
            cube([tablet_width, tablet_height, tablet_thickness]);
            translate([wood_thickness, wood_thickness, wood_thickness])
                cube([tablet_width-2*wood_thickness, tablet_height-2*wood_thickness, tablet_thickness-wood_thickness+1]);
        }

        // Corner reinforcements
        for(x = [0, tablet_width-wood_thickness]) {
            for(y = [0, tablet_height-wood_thickness]) {
                translate([x, y, 0])
                    cube([wood_thickness, wood_thickness, tablet_thickness+1]);
            }
        }
    }
}

module wax_surface() {
    // Recessed wax area
    color(wax_color) {
        translate([wood_thickness+1, wood_thickness+1, tablet_thickness-wax_depth-1])
            cube([tablet_width-2*wood_thickness-2, tablet_height-2*wood_thickness-2, wax_depth+1]);
    }
}

module hinges() {
    // Bronze hinges connecting tablets
    color(bronze_color) {
        for(side = [0, 1]) {
            translate([side * (tablet_width-hinge_gap), -hinge_length/2, 0])
                rotate([90, 0, 0])
                    cylinder(h=hinge_gap, d=hinge_diameter, $fn=20);

            translate([side * (tablet_width-hinge_gap), -hinge_length/2, tablet_thickness-hinge_diameter])
                rotate([90, 0, 0])
                    cylinder(h=hinge_gap, d=hinge_diameter, $fn=20);
        }

        // Hinge pins
        translate([0, 0, tablet_thickness/2])
            rotate([90, 0, 0])
                cylinder(h=tablet_width, d=hinge_diameter*0.8, $fn=16);
    }
}

module stylus() {
    // Roman stylus for writing
    color(bronze_color) {
        translate([0, -tablet_height/2-10, 0])
            rotate([90, 0, 0]) {
                // Main shaft
                cylinder(h=stylus_length, d=stylus_diameter, $fn=12);

                // Flattened end for writing
                translate([0, 0, stylus_length-stylus_diameter*2])
                    scale([1, 0.3, 1])
                        cylinder(h=stylus_diameter*2, d=stylus_diameter*2, $fn=12);

                // Eraser end (rounded)
                translate([0, 0, 0])
                    sphere(d=stylus_diameter*1.5, $fn=12);
            }
    }
}

module decorative_motifs() {
    // Simple Roman decorative border pattern
    color(bronze_color) {
        for(i = [1:3]) {
            // Top border
            translate([i*20, 2, tablet_thickness-0.5])
                sphere(d=1.5, $fn=8);
            // Bottom border
            translate([i*20, tablet_height-2, tablet_thickness-0.5])
                sphere(d=1.5, $fn=8);
        }

        // Central boss
        translate([tablet_width/2, tablet_height/2, tablet_thickness-1])
            sphere(d=3, $fn=16);
    }
}

// Main assembly
union() {
    // Left tablet
    translate([-tablet_width/2-hinge_gap/2, 0, 0]) {
        wooden_frame();
        wax_surface();
        decorative_motifs();
    }

    // Right tablet
    translate([tablet_width/2+hinge_gap/2, 0, 0]) {
        wooden_frame();
        wax_surface();
        decorative_motifs();
    }

    // Hinges between tablets
    translate([0, 0, 0])
        hinges();

    // Stylus alongside
    translate([0, tablet_height/2+10, 0])
        stylus();
}

// Ancient Mesopotamian Clay Inventory Tablet (c. 2000 BCE)
// A cuneiform administrative tablet with inventory records

// Tablet dimensions (historical: ~906025 mm)
tablet_w = 80;
tablet_h = 55;
tablet_t = 20;

// Cuneiform wedge parameters
wedge_w = 2.5;
wedge_l = 4;
wedge_d = 1.2;

// Wall thickness for 3D printing
wall_t = 2;

module cuneiform_wedge(x, y, angle=0) {
    translate([x, y, tablet_t - wedge_d]) {
        rotate([0, 0, angle]) {
            // Wedge shape for cuneiform
            linear_extrude(height=wedge_d) {
                polygon(points=[
                    [-wedge_w/2, 0],
                    [wedge_w/2, 0],
                    [0, wedge_l]
                ]);
            }
        }
    }
}

module cuneiform_line(start_x, start_y, length, num_wedges) {
    for(i = [0:num_wedges-1]) {
        x = start_x + (i * length/num_wedges);
        cuneiform_wedge(x, start_y, rand() > 0.5 ? 0 : 180);
    }
}

module inventory_tablet() {
    difference() {
        // Main tablet body
        color("burlywood") {
            translate([-tablet_w/2, -tablet_h/2, 0]) {
                cube([tablet_w, tablet_h, tablet_t]);
            }
        }

        // Rounded edges for realism
        color("burlywood") {
            translate([-tablet_w/2 + 5, -tablet_h/2 + 5, 0]) {
                cylinder(h=tablet_t, r=5, $fn=16);
            }
            translate([tablet_w/2 - 5, -tablet_h/2 + 5, 0]) {
                cylinder(h=tablet_t, r=5, $fn=16);
            }
            translate([-tablet_w/2 + 5, tablet_h/2 - 5, 0]) {
                cylinder(h=tablet_t, r=5, $fn=16);
            }
            translate([tablet_w/2 - 5, tablet_h/2 - 5, 0]) {
                cylinder(h=tablet_t, r=5, $fn=16);
            }
        }

        // Seal impression area
        translate([tablet_w/4, tablet_h/4, tablet_t - 2]) {
            cylinder(h=2, r=8, $fn=32);
        }

        // Cuneiform text areas

        // Header - inventory title
        cuneiform_line(-tablet_w/3, tablet_h/4, tablet_w/1.5, 8);

        // Item counts - barley
        for(row = [0:2]) {
            y_pos = tablet_h/4 - (row * 8) - 8;
            cuneiform_line(-tablet_w/3, y_pos, tablet_w/3, 5);
            cuneiform_line(tablet_w/6, y_pos, tablet_w/6, 3);
        }

        // Item counts - livestock
        for(row = [3:4]) {
            y_pos = tablet_h/4 - (row * 8) - 8;
            cuneiform_line(-tablet_w/3, y_pos, tablet_w/3, 4);
            cuneiform_line(tablet_w/6, y_pos, tablet_w/6, 2);
        }

        // Date section
        cuneiform_line(-tablet_w/3, -tablet_h/3, tablet_w/3, 6);

        // Administrative signature
        cuneiform_line(tablet_w/6, -tablet_h/3, tablet_w/4, 7);
    }

    // Clay tablet texture - small imperfections
    for(i = [0:20]) {
        translate([
            rand() * tablet_w - tablet_w/2,
            rand() * tablet_h - tablet_h/2,
            tablet_t - rand() * 0.5
        ]) {
            sphere(r=rand() * 0.8 + 0.2);
        }
    }
}

// Render the tablet
inventory_tablet();

// Dead Sea Scrolls inspired parchment roll (historical artifact)
// Realistic style, medium complexity, 3D printable

$fn = 36;  // Smooth curves for 3D printing

// Main roll dimensions
roll_length = 80;
roll_diameter = 18;
wall_thickness = 2;

// Color for aged parchment
color([0.91, 0.85, 0.69]) {

    // Main parchment roll body
    difference() {
        // Outer cylinder for the rolled parchment
        cylinder(h = roll_length, d = roll_diameter, center = true);

        // Hollow center
        translate([0, 0, -1])
            cylinder(h = roll_length + 2, d = roll_diameter - wall_thickness*2, center = true);
    }

    // Visible edge of rolled parchment layers
    for (i = [0:2]) {
        rotate([0, 90, 0])
            translate([0, 0, -roll_length/2 + i*3 + 2])
                difference() {
                    cylinder(h = 1, d1 = roll_diameter - i*0.5, d2 = roll_diameter - i*0.5 - 0.5, center = true);
                    translate([0, 0, -1])
                        cylinder(h = 3, d = roll_diameter - wall_thickness*2 - i, center = true);
                }
    }

    // Unrolled parchment strip at the end
    translate([roll_diameter/2 - wall_thickness, 0, roll_length/2 - 5])
        rotate([90, 0, 0])
            linear_extrude(height = wall_thickness, center = true)
                polygon(points = [
                    [0, 0],
                    [0, 15],
                    [1, 15],
                    [2, 14],
                    [3, 15],
                    [4, 14],
                    [5, 15],
                    [6, 14],
                    [7, 15],
                    [8, 14],
                    [9, 15],
                    [10, 0]
                ]);

    // Torn edge detail at the beginning
    translate([0, 0, -roll_length/2 + 2])
        difference() {
            cylinder(h = 1, d = roll_diameter, center = true);
            for (i = [0:5]) {
                rotate([0, 0, i*60 + 30])
                    translate([roll_diameter/2 - 2, 0, 0])
                        cube([4, 3, 2], center = true);
            }
        }

    // Ancient Hebrew inscription lines (simplified)
    color([0.7, 0.6, 0.5]) {
        for (line = [0:3]) {
            rotate([90, 0, 0])
                translate([0, -roll_length/2 + line*8 + 10, roll_diameter/2 - wall_thickness/2])
                    for (char = [0:5]) {
                        translate([char*2.5 - 7.5, 0, 0])
                            cube([0.5, 0.2, 2]);
                    }
        }
    }

    // Leather ties/wrapping
    color([0.4, 0.25, 0.15]) {
        // Main leather cord
        rotate([90, 0, 0])
            translate([0, 0, roll_diameter/2 + 1])
                torus(roll_diameter/2 + 1, 1);

        // Knot detail
        translate([roll_diameter/2 + 1, 0, 0])
            sphere(d = 3);
    }
}

// Helper module for leather tie torus
module torus(r, thickness) {
    rotate_extrude()
        translate([r, 0, 0])
            circle(thickness);
}

