// Rotulus (ancient scroll) - historical artifact
// Medium complexity realistic style for 3D printing

// Overall dimensions
scroll_length = 80;
scroll_diameter = 12;
handle_length = 15;

// Colors
wood_color = [139/255, 69/255, 19/255];
parchment_color = [245/255, 222/255, 179/255];
ink_color = [25/255, 25/255, 25/255];
bronze_color = [205/255, 127/255, 50/255];

// Main scroll module
module rotulus() {
    // Left wooden handle
    translate([-scroll_length/2 - handle_length/2, 0, 0])
        color(wood_color)
        cylinder(h=handle_length, d=scroll_diameter, $fn=20);

    // Right wooden handle
    translate([scroll_length/2 + handle_length/2, 0, 0])
        color(wood_color)
        cylinder(h=handle_length, d=scroll_diameter, $fn=20);

    // Scroll body (rolled parchment)
    color(parchment_color) {
        // Main scroll tube
        cylinder(h=scroll_length, d=scroll_diameter, center=true, $fn=50);

        // Layered scroll effect
        for(i = [0:4]) {
            rotate([0, 90, 0])
            translate([0, 0, -scroll_length/2 + i*2 + 1])
            scale([1.05 - i*0.02, 1.05 - i*0.02, 0.5])
            cylinder(h=scroll_length - 2, d=scroll_diameter, center=false, $fn=50);
        }
    }

    // Bronze end caps
    color(bronze_color) {
        // Left cap
        translate([-scroll_length/2 - handle_length/2, 0, 0])
        rotate([90, 0, 0])
        cylinder(h=scroll_diameter/3, d=scroll_diameter*1.1, center=true, $fn=30);

        // Right cap
        translate([scroll_length/2 + handle_length/2, 0, 0])
        rotate([90, 0, 0])
        cylinder(h=scroll_diameter/3, d=scroll_diameter*1.1, center=true, $fn=30);
    }

    // Decorative bronze rings
    color(bronze_color) {
        for(pos = [-scroll_length/3, 0, scroll_length/3]) {
            rotate([0, 90, 0])
            translate([pos, 0, 0])
            difference() {
                cylinder(h=3, d=scroll_diameter*1.15, center=true, $fn=30);
                cylinder(h=4, d=scroll_diameter*0.9, center=true, $fn=30);
            }
        }
    }

    // Ink inscriptions
    color(ink_color) {
        for(i = [0:8]) {
            rotate([0, 90, 0])
            translate([0, scroll_diameter/2 + 0.1, -scroll_length/2 + 5 + i*8])
            cube([0.5, 0.2, 4], center=true);
        }

        // Decorative pattern
        for(j = [0:5]) {
            rotate([0, 90, 0])
            translate([0, scroll_diameter/2 + 0.1, -scroll_length/2 + 2 + j*13])
            for(k = [0:3]) {
                rotate([0, 0, k*45])
                translate([0, 0, 0])
                cube([0.3, 0.2, 1], center=true);
            }
        }
    }
}

// Create the rotulus
rotulus();

// OpenSCAD model of a Roman rotula (hand-washing basin)
// Historical artifact from Pompeii, 1st century AD
// Dimensions: 80mm diameter, 40mm height

// Main body parameters
diameter = 80;
height = 40;
wall_thickness = 3;
base_thickness = 5;

// Decorative elements
rim_width = 6;
handle_length = 15;
handle_width = 8;
handle_height = 6;
foot_diameter = 40;
foot_height = 8;

// Color definitions
bronze_color = [0.65, 0.35, 0.15];
patina_color = [0.2, 0.4, 0.3];

module rotula_base() {
    // Main bowl body
    difference() {
        // Outer bowl
        cylinder(h=height, d=diameter, $fn=64);

        // Inner cavity
        translate([0, 0, wall_thickness])
        cylinder(h=height-wall_thickness, d=diameter-2*wall_thickness, $fn=64);

        // Flatten bottom
        translate([0, 0, -0.1])
        cube([diameter+2, diameter+2, 0.2], center=true);
    }
}

module decorative_rim() {
    // Raised rim with stepped profile
    difference() {
        // Outer rim
        translate([0, 0, height])
        cylinder(h=rim_width, d=diameter+4, $fn=64);

        // Inner cutout
        translate([0, 0, height+rim_width-2])
        cylinder(h=4, d=diameter-2, $fn=64);
    }
}

module handles() {
    // Two opposing handles with classical scroll design
    for (angle = [0, 180]) {
        rotate([0, 0, angle])
        translate([diameter/2 - handle_length/2, 0, height/2 + 5])
        union() {
            // Main handle body
            rotate([0, 90, 0])
            cylinder(h=handle_length, d=handle_width, $fn=32);

            // Decorative scroll ends
            translate([handle_length/2, 0, 0])
            rotate([0, 90, 0])
            sphere(d=handle_width*1.2, $fn=32);

            translate([-handle_length/2, 0, 0])
            rotate([0, 90, 0])
            sphere(d=handle_width*1.2, $fn=32);
        }
    }
}

module foot_base() {
    // Classical trumpet-shaped foot
    difference() {
        // Main foot shape
        union() {
            cylinder(h=foot_height, d=foot_diameter+4, $fn=64);
            translate([0, 0, foot_height])
            cylinder(h=foot_height/2, d=foot_diameter, $fn=64);
        }

        // Hollow center
        translate([0, -0.1, 0])
        cylinder(h=foot_height*1.5, d=foot_diameter-8, $fn=64);
    }
}

module decorative_patterns() {
    // Classical acanthus leaf pattern around rim
    for (i = [0:15]) {
        rotate([0, 0, i*22.5])
        translate([diameter/2 + 2, 0, height+rim_width/2])
        scale([0.3, 0.3, 0.8])
        rotate([90, 0, 0])
        cylinder(h=8, d1=3, d2=1, $fn=3);
    }
}

module patina_effect() {
    // Random patina spots for realistic aging
    for (i = [0:20]) {
        rotate([0, 0, rands(0, 360, 1)[0]])
        translate([rands(0, diameter/2-5, 1)[0], 0, rands(wall_thickness, height-2, 1)[0]])
        scale([1, 1, 0.3])
        sphere(d=rands(2, 4, 1)[0], $fn=16);
    }
}

// Main assembly
