// Roman Wax Tablet (Tabula Cerata) - Historical Charta Artifact
// Medium complexity, realistic style
// A traditional Roman writing tablet with wax surface and wooden frame

// Overall dimensions for 3D printing
tablet_width = 80;
tablet_height = 60;
tablet_thickness = 10;

// Wax surface dimensions
wax_depth = 3;
border_width = 4;

// Hinge details
hinge_diameter = 6;
hinge_length = 15;
hinge_gap = 2;

// Stylus hole
stylus_hole_diameter = 4;

// Decorative elements
corner_radius = 3;
inscription_depth = 0.5;

// Wooden frame with wax cavity
module wax_tablet() {
    difference() {
        // Main wooden tablet
        color("burlywood")
        cube([tablet_width, tablet_height, tablet_thickness], center=true);

        // Wax cavity
        color("burlywood")
        translate([0, 0, tablet_thickness/2 - wax_depth/2])
        cube([tablet_width - 2*border_width, tablet_height - 2*border_width, wax_depth + 0.1], center=true);

        // Stylus hole
        rotate([90, 0, 0])
        translate([tablet_width/2 - border_width, 0, tablet_thickness/2 - wax_depth])
        cylinder(h=border_width*2, d=stylus_hole_diameter, $fn=16);
    }

    // Wax surface
    color("darkgoldenrod")
    translate([0, 0, tablet_thickness/2 - wax_depth - 0.05])
    cube([tablet_width - 2*border_width - 1, tablet_height - 2*border_width - 1, wax_depth], center=true);
}

// Hinge loops for binding tablets together
module hinge_loops() {
    // Left side loops
    for(i = [-1, 1]) {
        translate([-tablet_width/2 - hinge_gap, i*tablet_height/4, 0])
        rotate([90, 0, 0])
        color("burlywood")
        difference() {
            cylinder(h=hinge_length, d=hinge_diameter, $fn=16);
            // Hollow center
            cylinder(h=hinge_length + 0.1, d=hinge_diameter - 2, $fn=16);
        }
    }

    // Right side loops (offset for interlocking)
    for(i = [-0.5, 0.5]) {
        translate([tablet_width/2 + hinge_gap, i*tablet_height/3, 0])
        rotate([90, 0, 0])
        color("burlywood")
        difference() {
            cylinder(h=hinge_length, d=hinge_diameter, $fn=16);
            cylinder(h=hinge_length + 0.1, d=hinge_diameter - 2, $fn=16);
        }
    }
}

// Decorative corner reinforcements
module corner_reinforcements() {
    for(x = [-1, 1]) {
        for(y = [-1, 1]) {
            translate([x*(tablet_width/2 - border_width/2), y*(tablet_height/2 - border_width/2), 0])
            color("saddlebrown")
            difference() {
                // Corner bracket
                cube([border_width*2, border_width*2, tablet_thickness], center=true);

                // Rounded corners
                translate([border_width/2, border_width/2, 0])
                sphere(r=corner_radius, $fn=16);
                translate([-border_width/2, border_width/2, 0])
                sphere(r=corner_radius, $fn=16);
                translate([border_width/2, -border_width/2, 0])
                sphere(r=corner_radius, $fn=16);
                translate([-border_width/2, -border_width/2, 0])
                sphere(r=corner_radius, $fn=16);
            }
        }
    }
}

// Roman inscription on frame
module inscription() {
    // Top inscription
    translate([0, tablet_height/2 - border_width/2, tablet_thickness/2 - wax_depth - inscription_depth/2])
    linear_extrude(height=inscription_depth + 0.1)
    text("TABULA", size=4, halign="center", valign="center", font="Liberation Sans:style=Bold");

    // Bottom inscription
    translate([0, -tablet_height/2 + border_width/2, tablet_thickness/2 - wax_depth - inscription_depth/2])
    linear_extrude(height=inscription_depth + 0.1)
    text("CERATA", size=4, halign="center", valign="center", font="Liberation Sans:style=Bold");
}

// Wood grain texture effect using small cylinders
module wood_grain() {
    for(x = [-tablet_width/2:2:tablet_width/2]) {
        for(y = [-tablet_height/2:2:tablet_height/2]) {
            if(abs(x) > tablet_width/2 - border_width || abs(y) > tablet_height/2 - border_width) {
                translate([x, y, tablet_thickness/2 - wax_depth - 0.1])
                color("saddlebrown")
                cylinder(h=0.2, d=0.5, $fn=8);
            }
        }
    }
}

// Assemble the complete tablet
union() {
    wax_tablet();
    hinge_loops();
    corner_reinforcements();
    inscription();
    wood_grain();
}

