tablet_height = 50;
tablet_thickness = 8;
hinge_radius = 2.5;

// Wax layer thickness
wax_thickness = 2;

// Border decoration
border_width = 3;
corner_radius = 5;

// Stylus hole
stylus_hole_radius = 3;

// Color definitions
color_wood = [139/255, 90/255, 43/255];
color_wax = [75/255, 60/255, 45/255];
color_metal = [180/255, 180/255, 180/255];

module rounded_cube(width, height, depth, radius) {
    difference() {
        cube([width, height, depth]);
        // Round corners
        translate([radius, radius, -1]) cylinder(r=radius, h=depth+2);
        translate([width-radius, radius, -1]) cylinder(r=radius, h=depth+2);
        translate([radius, height-radius, -1]) cylinder(r=radius, h=depth+2);
        translate([width-radius, height-radius, -1]) cylinder(r=radius, h=depth+2);
    }
}

module single_tablet() {
    difference() {
        // Wooden tablet base
        color(color_wood) rounded_cube(tablet_width, tablet_height, tablet_thickness, corner_radius);

        // Wax recess
        translate([border_width, border_width, tablet_thickness-wax_thickness]) 
            cube([tablet_width-2*border_width, tablet_height-2*border_width, wax_thickness+1]);

        // Decorative border grooves
        for(i = [1:3]) {
            translate([border_width-i, border_width-i, tablet_thickness-1]) 
                cube([tablet_width-2*(border_width-i), tablet_height-2*(border_width-i), 0.5]);
            translate([border_width+i, border_width+i, tablet_thickness-1]) 
                cube([tablet_width-2*(border_width+i), tablet_height-2*(border_width+i), 0.5]);
        }

        // Stylus hole on right edge
        translate([tablet_width-stylus_hole_radius-2, tablet_height/2, tablet_thickness/2]) 
            rotate([90, 0, 0]) cylinder(r=stylus_hole_radius, h=10, center=true);
    }

    // Wax layer in recess
    translate([border_width, border_width, tablet_thickness-wax_thickness]) 
        color(color_wax) cube([tablet_width-2*border_width, tablet_height-2*border_width, wax_thickness]);
}

module hinge_pin() {
    // Metal hinge pin
    color(color_metal) cylinder(r=hinge_radius, h=tablet_width*0.7, center=false);
}

// Create the diptych (two tablets joined by hinge)
