        // Main body - folded parchment
        difference() {
            // Outer envelope shape
            cube([letter_width, letter_height, fold_thickness], center=true);

            // Inner hollow space (where letter would be folded)
            translate([0, 0, fold_thickness/2 - 0.1])
                cube([letter_width-4, letter_height-4, fold_thickness], center=true);
        }

        // Top flap (folded down)
        translate([0, letter_height/2 - fold_thickness/2, 0])
            rotate([45, 0, 0])
            cube([letter_width-2, letter_height/3, letter_thickness], center=true);

        // Bottom flap (folded up)
        translate([0, -letter_height/2 + fold_thickness/2, 0])
            rotate([-45, 0, 0])
            cube([letter_width-2, letter_height/3, letter_thickness], center=true);
    }
