module binding_cord() {
    color(cord_color) {
        // Horizontal band
        for(i = [-1, 1]) {
            translate([0, i * (letter_height/2 - 5), fold_thickness/2 + cord_thickness/2])
                rotate([90, 0, 0])
                cylinder(h=letter_width+2, d=cord_thickness, $fn=16);
