            rotate([0, 90, 0])
            cylinder(h=letter_height-10, d=cord_thickness, $fn=16);

        // Knot detail
        translate([letter_width/2 - 5, letter_height/2 - 5, fold_thickness/2])
            sphere(d=cord_thickness*2, $fn=16);
    }
}

