            cube([width, height, thickness], center = true);
            // Hollow for wax
            translate([0, 0, thickness/2 - 1])
                cube([width - 6, height - 6, 2], center = true);
        }

        // Hinges on spine
        for(i = [-1, 1]) {
            translate([is_left ? width/2 - 2 : -width/2 + 2, i * height/4, 0])
                rotate([0, 90, 0])
                cylinder(h = hinge_length, d = hinge_diameter, center = true);
        }

        // Decorative edge pattern
        for(x = [-width/2 + 5 : 5 : width/2 - 5]) {
            for(y = [-height/2 + 5 : 5 : height/2 - 5]) {
                if(abs(x) != width/2 - 5 || abs(y) != height/2 - 5) {
                    translate([x, y, thickness/2])
                        cylinder(h = 0.5, d = 2, $fn = 6);
                }
            }
        }
    }

    // Wax surface
    color("#2F1B14") {
        translate([0, 0, thickness/2 - 0.5])
            cube([width - 6, height - 6, 1], center = true);
    }

