module book_cover() {
    // Wooden cover with rounded edges
    color(wood_color) {
        translate([0, 0, 0]) {
            difference() {
                // Main cover block
                cube([book_width, book_height, 3], center = true);

                // Rounded edges by subtracting cylinders
                translate([book_width/2 - 1.5, 0, 0])
                    rotate([0, 90, 0])
                        cylinder(h = book_height, r = 1.5, center = true);
                translate([-book_width/2 + 1.5, 0, 0])
                    rotate([0, 90, 0])
                        cylinder(h = book_height, r = 1.5, center = true);
            }
        }
    }

    // Metal corner reinforcements
    color(metal_color) {
        for(x = [-1, 1]) {
            for(y = [-1, 1]) {
                translate([x * (book_width/2 - 3), y * (book_height/2 - 3), 1.5]) {
                    difference() {
                        cube([6, 6, 1], center = true);
                        cylinder(h = 2, r = 2.5, center = true);
                    }
                }
