    color(metal_color) {
        translate([0, 0, 1.5]) {
            cylinder(h = 2, r = 8, center = true);
            translate([0, 0, 1])
                cylinder(h = 2, r = 6, center = true);
        }
    }
}

module book_spine() {
    // Spine with raised bands
    color(wood_color) {
        translate([-book_width/2 - 1.5, 0, 0]) {
            cube([3, book_height, page_thickness + 6], center = true);

            // Raised bands for sewing supports
            for(i = [-20, 0, 20]) {
