// Viking Runestone Log - Historical Artifact
// A carved log section with runic inscriptions and decorative patterns
// Based on 9th-11th century Scandinavian runestones

// Main parameters
log_length = 80;
log_radius = 20;
wall_thickness = 3;

module rune_carving(char, x, y, z, scale_factor) {
    // Simple rune representation using primitive shapes
    translate([x, y, z]) scale([scale_factor, scale_factor, scale_factor]) {
        if (char == "F") {
            // Fehu rune (wealth)
            cube([2, 8, 1], center = true);
            translate([0, 2.5, 0]) cube([8, 2, 1], center = true);
        }
        if (char == "U") {
            // Ur rune (aurochs)
            cube([2, 8, 1], center = true);
            translate([-3, 0, 0]) cube([2, 8, 1], center = true);
            translate([3, 0, 0]) cube([2, 8, 1], center = true);
        }
        if (char == "T") {
            // Tyr rune (god)
            cube([8, 2, 1], center = true);
            translate([0, 3, 0]) cube([2, 6, 1], center = true);
        }
    }
}

module viking_knot(x_offset, y_offset, z_offset, rotation) {
    // Decorative Viking knot pattern
    translate([x_offset, y_offset, z_offset]) rotate([0, 0, rotation]) {
        color("DarkGoldenrod") {
            // Interlaced pattern elements
            for (i = [-1, 1]) {
                translate([i * 5, 0, 0]) 
                    rotate([90, 0, 0]) 
                    cylinder(h = 10, r = 1.5, $fn = 8);
                translate([0, i * 5, 0]) 
                    rotate([0, 90, 0]) 
                    cylinder(h = 10, r = 1.5, $fn = 8);
            }
            // Center crossing
            rotate([45, 0, 0]) cylinder(h = 7, r = 1.5, $fn = 8);
            rotate([-45, 0, 0]) cylinder(h = 7, r = 1.5, $fn = 8);
        }
    }
}

// Main log body
color("SaddleBrown") {
    difference() {
        // Outer log shape
        cylinder(h = log_length, r = log_radius, $fn = 32);

        // Hollow center for 3D printing
        translate([0, 0, -1]) 
            cylinder(h = log_length + 2, r = log_radius - wall_thickness, $fn = 32);

        // Bark texture - irregular grooves
        for (i = [0:30]) {
            rotate([0, 0, i * 12]) {
                translate([log_radius - 1.5, 0, i * 2.5]) 
                    rotate([90, 0, 0]) 
                    cylinder(h = 3, r1 = 1.5, r2 = 0.5, $fn = 6);
            }
        }
    }
}

// Top face with runic inscription
color("Tan") {
    translate([0, 0, log_length - 0.5]) {
        // Runic text "FUTHARK" (first six runes)
        rune_carving("F", -25, 0, 0, 1.5);
        rune_carving("U", -15, 0, 0, 1.5);
        rune_carving("T", -5, 0, 0, 1.5);
        rune_carving("F", 5, 0, 0, 1.5);
        rune_carving("U", 15, 0, 0, 1.5);
        rune_carving("T", 25, 0, 0, 1.5);
    }
}

// Decorative knot patterns around the circumference
for (i = [0:3]) {
    angle = i * 90;
    z_pos = log_length * 0.3 + i * 15;
    viking_knot(
        cos(angle) * (log_radius - 3),
        sin(angle) * (log_radius - 3),
        z_pos,
        angle
    );
}

// Weathering and age marks
color("DimGray") {
    for (i = [0:20]) {
        rotate([0, 0, i * 18]) {
            translate([log_radius - 1, 0, i * 3.8]) {
                scale([1, 0.5, 0.3]) 
                    sphere(r = 1.5, $fn = 8);
            }
        }
    }
}

// Bottom base ring
color("Sienna") {
    translate([0, 0, 0]) {
        cylinder(h = 3, r = log_radius + 1, $fn = 32);
        translate([0, 0, -1]) 
            cylinder(h = 5, r = log_radius + 2, r1 = log_radius + 2, r2 = log_radius + 1, $fn = 32);
    }
}

// Babylonian cuneiform clay tablet with cuneiform impressions
