    difference() {
        // Raised area around wedge
        translate([0, 0, 0.2])
            cube([size*1.5, size*2, 0.4], center=true);

        // Wedge impression
        translate([0, 0, -0.1])
            scale([0.3, 1, 1])
                rotate([0, 90, 0])
                    cylinder(h=size*1.5, d=size, center=true);
