    clay_tablet();

    // Cuneiform text area - front
    for(row=[-15:8:15]) {
        for(col=[-25:6:25]) {
            if(row != 7 && !(abs(col) < 10 && row == -7)) {
                cuneiform_wedge(col, row, (row+col)%2*45);
            }
        }
    }

    // Cuneiform text area - back
    translate([0, 0, -12])
    rotate([180, 0, 0])
    for(row=[-12:8:12]) {
        for(col=[-20:6:20]) {
            cuneiform_wedge(col, row, (row+col)%2*-45);
        }
    }

    // Decorative edge elements
