// Create a row of cuneiform characters
module cuneiform_line(row, y_pos) {
    for(i=[0:len(row)-1]) {
        if(row[i] == "1") {
            wedge(-25 + i*5, y_pos, 0);
        }
        if(row[i] == "2") {
            wedge(-25 + i*5, y_pos, 45);
        }
        if(row[i] == "3") {
            wedge(-25 + i*5, y_pos, -45);
        }
    }
}

