    pattern = [
        ["1","2","3","1","2","1","3","2","1","1"],
        ["2","1","1","3","1","2","2","1","3","2"],
        ["3","2","1","2","3","1","1","2","2","3"],
        ["1","3","2","1","1","2","3","1","2","1"]
    ];

    for(row=[0:len(pattern)-1]) {
        cuneiform_line(pattern[row], -15 + row*10);
    }
