engraving_color = [0.4, 0.35, 0.3];  // Darker stone for engravings

// Main tablet body with rounded top
module tablet_body() {
    color(stone_color)
    union() {
        // Main rectangular body
        cube([tablet_width, tablet_thickness, tablet_height - tablet_width/2], center=true);

        // Rounded top (half cylinder)
        translate([0, 0, tablet_height/2 - tablet_width/4])
        rotate([90, 0, 0])
        cylinder(h=tablet_width, r=tablet_width/2, center=true);
    }
}

// Decorative border pattern
module decorative_border() {
    color(stone_color)
    for(i = [-1, 1]) {
        // Left and right borders
        translate([tablet_width/2 * i, 0, 0])
        cube([border_thickness, tablet_thickness + 0.1, tablet_height - tablet_width/2], center=true);

        // Top border segments (following curve)
        translate([tablet_width/2 * i * 0.7, 0, tablet_height/2 - tablet_width/4])
        rotate([90, 0, 0])
        cylinder(h=border_thickness, r=tablet_width/2 * 0.7, center=true);
    }

    // Bottom border
    translate([0, 0, -tablet_height/2 + tablet_width/4])
    cube([tablet_width + border_thickness*2, tablet_thickness + 0.1, border_thickness], center=true);
