    color(engraving_color)
    difference() {
        // Create engraving cuts
        for(i = [1:10]) {
            translate([
                (i <= 5 ? -tablet_width/4 : tablet_width/4),
                tablet_thickness/2 + 0.1,
                tablet_height/2 - tablet_width/4 - 8 - (i <= 5 ? i-1 : i-6) * 12
