// Medieval Wax Seal Contract
// A folded parchment with wax seal and cord, 14th century style

// Overall dimensions (mm)
contract_width = 40;
contract_height = 30;
contract_thickness = 3;
seal_diameter = 15;
seal_thickness = 4;

module parchment_sheet() {
    // Main contract body - slightly curved folded parchment
    color("antique_white") 
    difference() {
        // Main folded shape
        union() {
            // Front page
            translate([0, 0, contract_thickness/2]) 
            cube([contract_width, contract_height, contract_thickness], center=true);

            // Back page (folded behind)
            translate([0, -contract_height/2 - 0.5, -contract_thickness/2]) 
            rotate([10, 0, 0])
            cube([contract_width, contract_height, contract_thickness], center=true);
        }

        // Text lines on front (engraved)
        for(y = [-contract_height/3, -contract_height/6, 0, contract_height/6, contract_height/3]) {
            translate([0, y, contract_thickness/2 + 0.1]) 
            cube([contract_width * 0.8, 1, 0.2], center=true);
        }

        // Signature line at bottom
        translate([0, contract_height/3 + 5, contract_thickness/2 + 0.1]) 
        cube([contract_width * 0.6, 1, 0.2], center=true);
    }
}

module wax_seal() {
    // Red wax seal with seal design
    color("firebrick") {
        // Main seal body
        translate([0, -contract_height/2 - 5, -seal_thickness/2]) 
        cylinder(d=seal_diameter, h=seal_thickness);

        // Seal impression - heraldic rose
        difference() {
            // Base of seal
            translate([0, -contract_height/2 - 5, seal_thickness/2 - 0.5]) 
            cylinder(d=seal_diameter * 0.9, h=1);

            // Rose petals pattern
            for(i = [0:60:359]) {
                rotate([0, 0, i]) 
                translate([seal_diameter * 0.25, 0, -0.3]) 
                scale([1, 0.5, 1]) 
                sphere(d=seal_diameter * 0.3);
            }
        }
    }
}

module sealing_cord() {
    // Hemp cord attaching seal to contract
    color("saddlebrown") {
        // Cord from seal to contract
        hull() {
            translate([seal_diameter/2 - 2, -contract_height/2 - 5, 0]) 
            sphere(d=1.5);
            translate([contract_width/2 - 5, contract_height/2 - 5, contract_thickness/2]) 
            sphere(d=1.5);
        }

        // Cord loop around contract
        for(angle = [0:30:359]) {
            rotate([90, 0, angle]) 
            translate([contract_width/2 - 4, 0, 0]) 
            rotate([0, 90, 0]) 
            cylinder(h=1, d=1.5);
        }
    }
}

module decorative_edge() {
    // Decorative punched holes along edge
    color("antique_white") 
    for(x = [-contract_width/2 + 3:4:contract_width/2 - 3]) {
        translate([x, contract_height/2, contract_thickness/2]) 
        cylinder(h=contract_thickness + 0.5, d=1.5);
    }
}

// Assemble the contract
