// Main scroll body
module scroll_body() {
    color(parchment_color) {
        // Rolled parchment tube
        difference() {
            cylinder(h = scroll_length, d = scroll_diameter, center = true);
            // Hollow center
            cylinder(h = scroll_length + 2, d = scroll_diameter - 4, center = true);
        }

        // Parchment end details
        translate([scroll_length/2 - 2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = scroll_diameter + 2, d = 2, center = true);

        translate([-scroll_length/2 + 2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = scroll_diameter + 2, d = 2, center = true);
    }
}

// Wax seal with royal impression
module royal_seal() {
    color(seal_color) {
        // Main seal disk
        translate([scroll_length/2 - seal_diameter/2, 0, scroll_diameter/2 + seal_thickness/2])
            cylinder(h = seal_thickness, d = seal_diameter);

        // Royal crown impression on seal
        translate([scroll_length/2 - seal_diameter/2, 0, scroll_diameter/2 + seal_thickness]) {
            color(wax_color) {
                // Crown base
                cylinder(h = 1, d = seal_diameter - 4);

                // Crown points
                for(i = [-30:30:30]) {
                    rotate([0, 0, i])
                        translate([0, (seal_diameter - 6)/2, 0.5])
                            cube([2, 6, 2], center = true);
                }

                // Central orb
                sphere(d = 3);
            }
        }
    }
}

// Red silk ribbon
