    color(ribbon_color) {
        // Ribbon wrapping around scroll
        for(i = [0:120:240]) {
            rotate([0, 0, i])
                translate([scroll_length/2 - 10, 0, scroll_diameter/2 + 1])
                    cube([20, 2, 0.5], center = true);
        }

        // Ribbon knot under seal
        translate([scroll_length/2 - seal_diameter/2, 0, scroll_diameter/2 - 1])
            cylinder(h = 2, d = 6);
    }
}

// Parchment texture lines
module parchment_lines() {
    color("#DEB887") {
        for(i = [-scroll_length/2 + 5:5:scroll_length/2 - 5]) {
            translate([i, 0, scroll_diameter/2 + 0.1])
                cube([0.5, scroll_diameter - 2, 0.1], center = true);
        }
    }
}

// Latin inscription band
