        cube([tablet_width, tablet_height, tablet_thickness], center=true);

        // Carved border recess
        color("burlywood")
        translate([0, 0, tablet_thickness/2 - border_depth/2])
        cube([tablet_width - 6, tablet_height - 6, border_depth], center=true);
    }
}

module wax_seal() {
    // Central red wax seal with rope impression
    color("firebrick") {
        translate([0, 0, tablet_thickness/2 + seal_height/2])
        cylinder(h = seal_height, d = seal_diameter, center=true);

        // Seal impression detail
        translate([0, 0, tablet_thickness/2 + seal_height - 0.5])
        cylinder(h = 1, d = seal_diameter * 0.7, center=true);
    }

    // Rope under seal
    color("saddlebrown")
    for(i = [-1, 1]) {
        rotate([90, 0, 45 * i])
        translate([0, 0, tablet_thickness/2 + 0.5])
        cylinder(h = seal_diameter * 0.8, d = 2, center=true);
