// Main colors
color("goldenrod") union() {

    // Central handle for the seal matrix
    module handle() {
        // Cylindrical grip
        cylinder(h = 12, d = 8, center = true);
        // Decorative end cap
        translate([0, 0, 6]) sphere(d = 10);
    }

    // Tudor rose decorative boss
    module tudor_rose() {
        union() {
            // Central rose
            color("firebrick") 
            for(i = [0:45:315]) {
                rotate([0, 0, i]) 
                translate([0, 6, 0]) 
                scale([1, 0.6, 1]) 
                cylinder(h = 3, d = 4, center = true);
            }
            // Central boss
            translate([0, 0, 1.5]) sphere(d = 3);
        }
    }

    // Seal face with recessed design
    module seal_face() {
        difference() {
            // Main seal disc
            cylinder(h = 5, d = 40, center = true);

            // Tudor royal arms
            color("dimgray") {
                // Shield outline (recessed)
                translate([0, 0, 2.5]) {
                    difference() {
                        cube([25, 30, 3], center = true);
                        // Inner cutout
                        cube([20, 25, 4], center = true);
                    }
                }

                // Lions passant (simplified heraldic)
                for(pos = [[-8, 5], [8, 5]]) {
                    translate(pos + [0, 0, 2.5]) {
                        // Lion body
                        cube([6, 12, 3], center = true);
                        // Lion head
                        translate([0, 7, 0]) sphere(d = 5);
                    }
                }

                // Fleur-de-lis
                for(pos = [[-8, -8], [0, -10], [8, -8]]) {
                    translate(pos + [0, 0, 2.5]) {
                        // Central petal
                        cube([3, 8, 3], center = true);
                        // Side petals
                        translate([-3, -2, 0]) cube([3, 6, 3], center = true);
                        translate([3, -2, 0]) cube([3, 6, 3], center = true);
                    }
                }

                // Latin inscription around edge
                translate([0, 0, 2.5]) {
                    for(angle = [0:30:330]) {
                        rotate([0, 0, angle]) 
                        translate([17, 0, 0]) 
                        cube([2, 1, 3], center = true);
                    }
                }
            }
        }
    }

    // Decorative rope border around seal
    module rope_border() {
        for(angle = [0:15:345]) {
            rotate([0, 0, angle]) 
            translate([20, 0, 2.5]) 
            sphere(d = 2);
        }
    }

