// Main module
module legion_standard() {
    union() {
        // Wooden staff
        color(color_wood)
        translate([0, 0, 0])
        cylinder(h = 85, r = 3, $fn = 16);

        // Bronze base ferrule
        color(color_bronze)
        translate([0, 0, -2])
        cylinder(h = 5, r1 = 4, r2 = 3.5, $fn = 16);

        // Eagle standard assembly
        translate([0, 0, 85])
        rotate([0, 0, 0])
        eagle_standard();

        // Cross-bar supports for banners
        color(color_bronze)
        translate([0, 0, 75])
        rotate([90, 0, 0])
        cylinder(h = 25, r = 1, $fn = 8);

        // Banner attachment rings
        for (i = [-1, 1]) {
            color(color_bronze)
            translate([i * 12, 0, 75])
            rotate([90, 0, 0])
            torus(1, 0.5);
        }
    }
