// Eagle module
module eagle_standard() {
    union() {
        // Bronze mount
        color(color_bronze)
        translate([0, 0, 0])
        cylinder(h = 5, r = 6, $fn = 32);

        // Eagle body
        color(color_gold)
        translate([0, 0, 5])
        scale([1, 0.7, 1.2])
        sphere(r = 4, $fn = 32);

        // Eagle head
        color(color_gold)
        translate([0, 3, 8])
        scale([0.8, 1.2, 0.8])
        sphere(r = 3, $fn = 32);

        // Beak
        color(color_gold)
        translate([0, 4.5, 8.5])
        rotate([30, 0, 0])
        cylinder(h = 2, r1 = 0.8, r2 = 0.3, $fn = 8);

        // Wings
        wing(-45);
        wing(45);

        // Tail feathers
        color(color_gold)
        translate([0, -3, 5])
        rotate([0, 0, 0])
        scale([1, 0.2, 0.5])
        cube([6, 1, 3], center = true);

        // Claws gripping thunderbolts
        thunderbolt(2, 0);
        thunderbolt(-2, 0);

        // Decorative wreath around base
        decorative_wreath();
    }
}

// Wing module
module wing(angle) {
    color(color_gold)
    translate([0, 0, 7])
    rotate([0, 0, angle])
    scale([1, 0.2, 0.5])
    cube([8, 1, 4], center = true);

    // Wing feathers detail
    for (i = [1:3]) {
        translate([i * 2 * cos(angle), i * 2 * sin(angle), 7 - i * 0.3])
        rotate([0, 0, angle])
        scale([0.8, 0.1, 0.4])
        cube([6, 0.5, 3], center = true);
    }
}

// Thunderbolt module
module thunderbolt(x_pos, z_rot) {
    color(color_gold)
    translate([x_pos, 0, 3])
    rotate([0, 0, z_rot])
    scale([0.3, 0.3, 1])
    cylinder(h = 3, r = 1.5, $fn = 6);

    // Zig-zag pattern
    for (i = [0.5:0.5:2.5]) {
        translate([x_pos + (i % 1 == 0 ? 0.5 : -0.5), 0, i])
        rotate([0, 0, z_rot])
        cube([0.5, 0.3, 0.5], center = true);
    }
}

// Decorative wreath module
module decorative_wreath() {
    color(color_bronze)
    translate([0, 0, 2.5])
    rotate([0, 0, 0])
    cylinder(h = 0.5, r = 7, r2 = 6.5, $fn = 64);

    // Laurel leaves
    for (i = [0:30:330]) {
        translate([cos(i) * 6.5, sin(i) * 6.5, 2.5])
        rotate([90, 0, i + 90])
        scale([0.3, 1, 0.1])
        cylinder(h = 1.5, r1 = 0.5, r2 = 0, $fn = 8);
    }
}

// Torus helper module
module torus(major, minor) {
    rotate_extrude($fn = 32)
    translate([major, 0, 0])
    circle(r = minor, $fn = 16);
}

