// Roman numerals for hours (I-VI across top)
module roman_numeral(num, x) {
    // Simple line-based numerals
    if (num == 1) {
        translate([x, -11, 2.5]) cube([0.5, 2, 0.5]);
    }
    if (num == 2) {
        translate([x, -11, 2.5]) cube([0.5, 2, 0.5]);
        translate([x+1, -11, 2.5]) cube([0.5, 2, 0.5]);
    }
    if (num == 3) {
        translate([x, -11, 2.5]) cube([0.5, 2, 0.5]);
        translate([x+1, -11, 2.5]) cube([0.5, 2, 0.5]);
        translate([x+2, -11, 2.5]) cube([0.5, 2, 0.5]);
    }
    if (num == 4) {
        translate([x, -11, 2.5]) cube([0.5, 3, 0.5]);
        translate([x+1, -11, 2.5]) cube([0.5, 2, 0.5]);
    }
    if (num == 5) {
        translate([x, -11, 2.5]) cube([0.5, 3, 0.5]);
    }
    if (num == 6) {
        translate([x, -11, 2.5]) cube([0.5, 3, 0.5]);
        translate([x+1, -11, 2.5]) cube([0.5, 2, 0.5]);
    }
}

// Place numerals
roman_numeral(1, -15);
roman_numeral(2, -9);
roman_numeral(3, -3);
roman_numeral(4, 3);
roman_numeral(5, 9);
roman_numeral(6, 15);

// Duty assignments (simple letter markings)
module duty_mark(x, y, letter) {
    // Simple block letters
    if (letter == "S") {
        translate([x, y, 2.5]) cube([2, 0.5, 0.5]);
        translate([x, y, 2.5]) cube([0.5, 2, 0.5]);
        translate([x+1.5, y, 2.5]) cube([0.5, 2, 0.5]);
        translate([x, y+1.5, 2.5]) cube([2, 0.5, 0.5]);
        translate([x, y+0.75, 2.5]) cube([2, 0.5, 0.5]);
    }
    if (letter == "G") {
        translate([x, y, 2.5]) cube([2, 0.5, 0.5]);
        translate([x, y, 2.5]) cube([0.5, 2, 0.5]);
        translate([x, y+1.5, 2.5]) cube([2, 0.5, 0.5]);
        translate([x+1.5, y+0.75, 2.5]) cube([0.5, 0.75, 0.5]);
        translate([x+1.5, y+1.5, 2.5]) cube([0.5, 0.5, 0.5]);
    }
}

duty_mark(-18, -2, "S");
duty_mark(-12, 2, "G");
duty_mark(-6, -6, "S");
duty_mark(6, 6, "G");
duty_mark(12, -2, "S");
duty_mark(18, 2, "G");

