// Appendix Historical Artifact - Roman Wax Tablet (Tabula)
// Realistic style, medium complexity
// Overall dimensions: 80mm x 60mm x 12mm

// Historical colors
tablet_color = "#8B4513";  // Saddle brown - wood
wax_color = "#2F1B14";     // Dark brown - beeswax
text_color = "#CD853F";    // Peru - lighter wood for stylus

// Main tablet body
module tablet_body() {
    difference() {
        // Main wooden tablet
        color(tablet_color)
        cube([80, 60, 10], center=true);

        // Recessed area for wax
        translate([0, 0, 4.5])
        cube([70, 50, 2], center=true);

        // Holes for binding cord
        for(i = [-1, 1]) {
            translate([35*i, 25, 0])
            rotate([90, 0, 0])
            cylinder(h=20, d=4, center=true);
        }
    }
}

// Wax surface
module wax_surface() {
    color(wax_color)
    translate([0, 0, 3])
    cube([68, 48, 1], center=true);
}

// Roman inscription strokes (simplified)
module inscription() {
    color("#1a0f0a")
    translate([0, 0, 3.6])
    for(x = [-30:15:30]) {
        for(y = [-20:10:20]) {
            if(abs(x) + abs(y) > 10) {
                rotate([0, 0, rand() * 30])
                cube([8, 1.5, 0.3], center=true);
            }
        }
    }
}

// Decorative border pattern
module border_pattern() {
    color(text_color)
    translate([0, 0, 5.1])
    for(i = [-1, 1]) {
        // Top and bottom borders
        for(x = [-30:5:30]) {
            translate([x, 27*i, 0])
            cube([3, 2, 0.5], center=true);
        }
        // Left and right borders
        for(y = [-25:5:25]) {
            translate([37.5*i, y, 0])
            cube([2, 3, 0.5], center=true);
        }
    }
}

// Stylus (writing tool)
module stylus() {
    color(text_color)
    translate([45, 0, -8])
    rotate([90, 0, 0])
    union() {
        // Handle
        cylinder(h=20, d=4, center=true);
        // Point
        translate([0, 0, 12])
        cylinder(h=8, d1=4, d2=1, center=true);
    }
}

// Assembly
union() {
    tablet_body();
    wax_surface();
    inscription();
    border_pattern();
    stylus();
}

// Bronze Age Clay Tablet Addendum - Cuneiform Receipt Tablet (circa 2500 BCE)
// Realistic style, medium complexity

// Overall dimensions (mm)
tablet_width = 60;
tablet_height = 40;
tablet_thickness = 15;

// Wall thickness for 3D printing
wall_thickness = 2;

module tablet_body() {
    difference() {
        // Main tablet shape - slightly curved for authenticity
        hull() {
            cube([tablet_width, tablet_height, tablet_thickness], center = true);
            translate([0, 0, tablet_thickness/2 - 2]) 
                sphere(d = tablet_width * 0.9);
        }

        // Hollow out for 3D printing
        translate([0, 0, 1])
            cube([tablet_width - wall_thickness*2, 
                  tablet_height - wall_thickness*2, 
                  tablet_thickness], center = true);
    }
}

module cuneiform_wedge(x, y, depth = 1) {
    // Single cuneiform wedge impression
    translate([x, y, tablet_thickness/2 - 0.5])
        rotate([0, 0, rand(0, 30)])
        linear_extrude(height = depth + 0.5)
            polygon(points = [[0, 0], [2, 3], [3, 0]]);
}

module cuneiform_numbers() {
    // Early Sumerian numeric system wedges
    for (i = [0:4]) {
        for (j = [0:2]) {
            cuneiform_wedge(-20 + i*10, -10 + j*8, 1.5);
        }
    }

    // Quantity symbols
    for (k = [0:5]) {
        translate([10 + k*3, -5, 0])
            rotate([90, 0, 0])
            cylinder(h = 1.5, r = 0.5, $fn = 6);
    }
}

module tablet_edge_markings() {
    // Decorative edge notches typical for receipts
    for (i = [0:7]) {
        rotate([0, 0, i*45])
        translate([tablet_width/2 - 2, 0, tablet_thickness/2 - 1])
            cube([4, 2, 3], center = true);
    }
}

module seal_impression() {
    // Cylinder seal impression at bottom
    translate([0, tablet_height/2 - 5, tablet_thickness/2 - 0.5])
    difference() {
        cylinder(h = 1.5, r = 8, $fn = 32);
        for (angle = [0:30:330]) {
            rotate([0, 0, angle])
            translate([5, 0, 0])
            sphere(d = 2);
        }
    }
}

module repair_holes() {
    // Authentic repair holes from ancient times
    for (pos = [[-15, 12], [15, -12]]) {
        translate([pos[0], pos[1], 0])
        rotate([90, 0, 0])
        cylinder(h = tablet_thickness, r = 1.5, $fn = 8);
    }
}

// Main assembly
color("#8B4513")  // Bronze age clay color
union() {
    tablet_body();

    color("#654321")  // Darker impressions
    union() {
        cuneiform_numbers();
        seal_impression();
        tablet_edge_markings();
    }

    color("#A0522D")  // Fired clay repair
    repair_holes();
}

// Add maker's mark on back
color("#4A4A4A")
translate([0, 0, -tablet_thickness/2 + 0.5])
    cylinder(h = 0.5, r = 3, $fn = 16);

// Random seed for natural variations
function rand(min, max) = min + (max - min) * (rands(0, 1, 1)[0]);

