// Ancient Roman Bronze Codicil (testamentary supplement)
// A small folding tablet used for legal amendments to wills

// Overall dimensions in mm
tablet_width = 45;
tablet_height = 65;
tablet_thickness = 3;
hinge_diameter = 4;

// Bronze material color
color([0.72, 0.45, 0.2])

// Main tablet body as two hinged leaves
module tablet_leaf(width, height, thickness) {
    difference() {
        // Main tablet body
        cube([width, height, thickness], center=true);

        // Wax surface recess
        translate([0, 0, thickness/2 - 0.5])
            cube([width - 6, height - 10, 1], center=true);

        // Rounded corners
        translate([width/2 - 3, height/2 - 3, 0])
            sphere(r=3, $fn=16);
        translate([-width/2 + 3, height/2 - 3, 0])
            sphere(r=3, $fn=16);
        translate([width/2 - 3, -height/2 + 3, 0])
            sphere(r=3, $fn=16);
        translate([-width/2 + 3, -height/2 + 3, 0])
            sphere(r=3, $fn=16);
    }
}

// Hinge mechanism
module hinge(diameter, length) {
    for(i = [0:2]) {
        translate([0, -length/2 + i * (length/3), 0])
            rotate([90, 0, 0])
                cylinder(h=diameter, r=diameter/2, center=true, $fn=12);
    }
}

// Decorative border pattern
module border_pattern(width, height) {
    // Top and bottom borders with rivet details
    for(i = [-width/2 + 5 : 5 : width/2 - 5]) {
        translate([i, height/2 - 3, tablet_thickness/2 - 0.5])
            cylinder(h=1, r=1, $fn=8);
        translate([i, -height/2 + 3, tablet_thickness/2 - 0.5])
            cylinder(h=1, r=1, $fn=8);
    }

    // Side borders with rivet details
    for(j = [-height/2 + 10 : 5 : height/2 - 10]) {
        translate([width/2 - 3, j, tablet_thickness/2 - 0.5])
            cylinder(h=1, r=1, $fn=8);
        translate([-width/2 + 3, j, tablet_thickness/2 - 0.5])
            cylinder(h=1, r=1, $fn=8);
    }
}

// Seal ring attachment point
module seal_ring() {
    translate([0, tablet_height/2 + 3, 0]) {
        difference() {
            // Ring base
            cylinder(h=3, r=4, $fn=32);
            // Ring hole
            translate([0, 0, -1])
                cylinder(h=5, r=2, $fn=32);
        }
    }
}

// Roman inscription panel
module inscription_panel() {
    translate([0, 0, tablet_thickness/2 + 0.1]) {
        // Raised border for inscription area
        cube([tablet_width - 12, tablet_height - 16, 0.5], center=true);

        // Text lines (simulated as grooves)
        for(line = [1:4]) {
            translate([0, -15 + line * 6, 0])
                cube([tablet_width - 16, 0.5, 0.2], center=true);
        }
    }
}

// Assembly
union() {
    // Left tablet leaf
    translate([-tablet_width/2 - 2, 0, 0])
        tablet_leaf(tablet_width, tablet_height, tablet_thickness);

    // Right tablet leaf
    translate([tablet_width/2 + 2, 0, 0])
        tablet_leaf(tablet_width, tablet_height, tablet_thickness);

    // Central hinge
    translate([0, 0, 0])
        hinge(hinge_diameter, tablet_height);

    // Decorative elements on left leaf
    translate([-tablet_width/2 - 2, 0, 0]) {
        border_pattern(tablet_width, tablet_height);
        inscription_panel();
    }

    // Decorative elements on right leaf
    translate([tablet_width/2 + 2, 0, 0]) {
        border_pattern(tablet_width, tablet_height);
        inscription_panel();
    }

    // Seal ring on right leaf
    translate([tablet_width/2 + 2, 0, 0])
        seal_ring();
}

// Roman wax tablet (tabula cerata) - 1st-2nd century CE
// A hinged wooden notebook with wax-filled recesses for writing with a stylus
// Designed for 3D printing at ~60mm length, ~40mm width when closed

// Overall tablet dimensions
tablet_length = 60;
tablet_width = 40;
tablet_thickness = 8;
wall_thickness = 2;
wax_depth = 3;

// Color definitions
wood_color = [0.4, 0.25, 0.1];
wax_color = [0.9, 0.7, 0.2];
metal_color = [0.7, 0.6, 0.4];

// Hinge pin
pin_radius = 1.5;
pin_length = tablet_width;

// Left half of tablet
module left_half() {
    difference() {
        // Main wooden frame
        color(wood_color)
        cube([tablet_length/2, tablet_width, tablet_thickness], center=true);

        // Wax cavity
        translate([0, 0, tablet_thickness/2 - wax_depth/2 - wall_thickness])
            color(wax_color)
            cube([tablet_length/2 - 2*wall_thickness, tablet_width - 2*wall_thickness, wax_depth], center=true);

        // Hinge holes
        translate([tablet_length/4, tablet_width/2, 0])
            rotate([90, 0, 0])
            cylinder(r=pin_radius, h=pin_length/2, center=true);
        translate([tablet_length/4, -tablet_width/2, 0])
            rotate([90, 0, 0])
            cylinder(r=pin_radius, h=pin_length/2, center=true);
    }
}

// Right half of tablet (mirror of left half)
module right_half() {
    mirror([1, 0, 0]) left_half();
}

// Hinge pin
module hinge_pin() {
    color(metal_color)
    translate([0, 0, 0])
        rotate([90, 0, 0])
        cylinder(r=pin_radius, h=pin_length, center=true);
}

// Decorative corner reinforcement plates
module corner_plates() {
    plate_size = 6;
    plate_thickness = 1;

    // Corners for left half
    for (x = [-1, 1]) {
        for (y = [-1, 1]) {
            translate([x * (tablet_length/4 - plate_size/2), 
                      y * (tablet_width/2 - plate_size/2), 
                      tablet_thickness/2 + plate_thickness/2])
                color(metal_color)
                cube([plate_size, plate_size, plate_thickness], center=true);
        }
    }

    // Corners for right half
    for (x = [-1, 1]) {
        for (y = [-1, 1]) {
            translate([-x * (tablet_length/4 - plate_size/2), 
                      y * (tablet_width/2 - plate_size/2), 
                      tablet_thickness/2 + plate_thickness/2])
                color(metal_color)
                cube([plate_size, plate_size, plate_thickness], center=true);
        }
    }
}

// Assembly
translate([0, 0, 0]) {
    left_half();
    right_half();
    corner_plates();
    hinge_pin();
}

