// Commentary Historical Artifact: Roman Wax Tablet Set with Bronze Hinges
// A realistic 3D printable model of a Roman wax tablet used for writing commentaries
// Size: 90mm width, 60mm height, 12mm thickness (closed)

// Main parameters
tablet_width = 80;
tablet_height = 55;
tablet_thickness = 4;
hinge_diameter = 4;
hinge_length = 8;
wax_depth = 2;
border_width = 3;

// Color definitions
bronze_color = [150/255, 100/255, 50/255];
wax_color = [40/255, 35/255, 30/255];
wood_color = [120/255, 100/255, 80/255];

// Create the complete wax tablet set
module wax_tablet_set() {
    // Left tablet
    translate([-tablet_width/2 - hinge_diameter, 0, 0]) {
        color(wood_color) tablet_half();
    }

    // Right tablet
    translate([tablet_width/2 + hinge_diameter, 0, 0]) {
        color(wood_color) tablet_half();
    }

    // Bronze hinges
    color(bronze_color) {
        // Top hinge
        translate([0, tablet_height/2 - 5, 0]) {
            hinge();
        }

        // Bottom hinge
        translate([0, -tablet_height/2 + 5, 0]) {
            hinge();
        }
    }
}

// Create one half of the tablet
module tablet_half() {
    difference() {
        // Main wooden tablet
        cube([tablet_width, tablet_height, tablet_thickness], center=true);

        // Wax recess
        translate([0, 0, tablet_thickness/2 - wax_depth/2]) {
            cube([tablet_width - 2*border_width, tablet_height - 2*border_width, wax_depth + 0.1], center=true);
        }

        // Decorative border pattern
        for(i = [0:5]) {
            translate([tablet_width/2 - border_width - i*2, tablet_height/2 - border_width - 1, tablet_thickness/2 - 0.5]) {
                cube([1, 2, 1]);
            }
            translate([tablet_width/2 - border_width - 1, tablet_height/2 - border_width - i*2, tablet_thickness/2 - 0.5]) {
                cube([2, 1, 1]);
            }
        }
    }

    // Wax surface
    color(wax_color) {
        translate([0, 0, tablet_thickness/2 - wax_depth/2]) {
            cube([tablet_width - 2*border_width - 2, tablet_height - 2*border_width - 2, wax_depth], center=true);
        }
    }
}

// Bronze hinge mechanism
module hinge() {
    union() {
        // Central pin
        rotate([90, 0, 0]) {
            cylinder(h=hinge_length * 1.2, d=hinge_diameter, center=true);
        }

        // Left hinge loops
        for(i = [-1, 0, 1]) {
            translate([-tablet_width/2 - hinge_diameter/2, i*2.5, 0]) {
                rotate([90, 0, 0]) {
                    difference() {
                        cylinder(h=hinge_length, d=hinge_diameter + 2, center=true);
                        cylinder(h=hinge_length + 1, d=hinge_diameter, center=true);
                    }
                }
            }
        }

        // Right hinge loops
        for(i = [-1, 0, 1]) {
            translate([tablet_width/2 + hinge_diameter/2, i*2.5 + 1.25, 0]) {
                rotate([90, 0, 0]) {
                    difference() {
                        cylinder(h=hinge_length, d=hinge_diameter + 2, center=true);
                        cylinder(h=hinge_length + 1, d=hinge_diameter, center=true);
                    }
                }
            }
        }
    }
}

// Create the complete artifact
wax_tablet_set();

// Scholium - Medieval manuscript gloss artifact
// A realistic representation of a 13th century scholium with marginal notes

// Overall dimensions: 80mm x 60mm x 8mm

// Colors
color_parchment = [0.95, 0.90, 0.82];
color_ink = [0.25, 0.20, 0.15];
color_red = [0.65, 0.15, 0.10];
color_blue = [0.10, 0.20, 0.55];

// Main manuscript page with scholium
module manuscript_page() {
    color(color_parchment)
    difference() {
        // Main page
        cube([80, 60, 2], center=false);

        // Binding holes
        for (i = [10:20:50]) {
            translate([i, 2, 0])
            cylinder(h=3, d=2, center=false);
        }
    }
}

// Main text block (latin text representation)
module main_text() {
    color(color_ink)
    translate([10, 10, 2.1])
    cube([55, 35, 0.5]);
}

// Individual gloss note
module gloss_note(x, y, width, height) {
    color(color_ink)
    translate([x, y, 2.1])
    cube([width, height, 0.3]);

    // Red initial letter decoration
    color(color_red)
    translate([x, y, 2.4])
    cube([3, 3, 0.2]);
}

// Decorative initial with illumination
module illuminated_initial() {
    // Blue background
    color(color_blue)
    translate([12, 12, 2.4])
    cube([8, 8, 0.5]);

    // Gold pattern (represented with yellow)
    color([0.8, 0.7, 0.1])
    for (i = [0:3]) {
        translate([14 + i*1.5, 14, 2.9])
        sphere(d=0.8);
    }
}

// Red and blue paragraph markers
module paragraph_markers() {
    // Red marker
    color(color_red)
    translate([65, 20, 2.1])
    cylinder(h=0.5, d=2, center=false);

    // Blue marker
    color(color_blue)
    translate([65, 28, 2.1])
    cylinder(h=0.5, d=2, center=false);
}

// Binding cord impression
module binding_cord() {
    color([0.4, 0.3, 0.2])
    translate([0, 30, 1])
    rotate([0, 90, 0])
    cylinder(h=80, d=1.5, center=false);
}

// Generate the complete scholium
manuscript_page();
main_text();
illuminated_initial();

// Multiple gloss notes in margins
gloss_note(5, 20, 4, 15);
gloss_note(70, 15, 6, 8);
gloss_note(20, 5, 30, 3);
gloss_note(25, 52, 25, 4);

paragraph_markers();
binding_cord();

// Additional marginal decoration
color(color_red)
translate([75, 45, 2.1])
rotate([0, 0, 45])
cube([2, 8, 0.3]);

color(color_blue)
translate([5, 45, 2.1])
rotate([0, 0, -30])
cube([2, 6, 0.3]);

// Marginalia - Medieval Manuscript Grotesque
// A 3D printable interpretation of a historiated initial marginal grotesque
// Based on 13th century illuminated manuscripts

$fn = 32; // Smooth curves for 3D printing

// Main colors
color_brown = [0.4, 0.25, 0.15];
color_gold = [0.8, 0.6, 0.2];
color_red = [0.6, 0.1, 0.1];
color_parchment = [0.95, 0.9, 0.8];

// Overall dimensions for 3D printing
scale_factor = 0.5; // Final size ~60mm

module marginalia_grotesque() {
    // Base manuscript corner
    color(color_parchment)
    translate([0, 0, -2])
    cube([40, 30, 2], center=false);

    // Main dragon body
    color(color_brown)
    union() {
        // Serpentine body
        rotate([0, 0, 15])
        translate([8, 5, 0])
        cylinder(h=25, d1=8, d2=6);

        // Body curve
        rotate([0, 0, 45])
        translate([15, 8, 8])
        cylinder(h=20, d1=6, d2=4);

        // Tail curl
        rotate([0, 0, 90])
        translate([20, 15, 15])
        cylinder(h=15, d1=4, d2=2);
    }

    // Dragon head
    color(color_brown)
    union() {
        // Snout
        rotate([0, -20, 35])
        translate([5, 2, 20])
        cylinder(h=8, d1=6, d2=4);

        // Head bulb
        rotate([0, -20, 35])
        translate([3, 1, 26])
        sphere(d=7);
    }

    // Horns
    color(color_brown)
    for(i = [0:1]) {
        rotate([0, -20, 35 + i*20])
        translate([3 + i*2, 1 + i*1, 29])
        cylinder(h=6, d1=2, d2=1);
    }

    // Wings
    color(color_brown)
    union() {
        // Left wing
        rotate([30, -15, 20])
        translate([10, 8, 15])
        cube([12, 2, 8]);

        // Wing detail
        rotate([30, -15, 25])
        translate([12, 9, 16])
        cube([8, 1.5, 6]);

        // Right wing
        rotate([30, -15, 50])
        translate([12, 6, 12])
        cube([10, 2, 7]);
    }

    // Decorative gold leaf accents
    color(color_gold)
    union() {
        // Eye
        rotate([0, -20, 35])
        translate([4, 2, 25])
        sphere(d=1.5);

        // Tail decoration
        rotate([0, 0, 90])
        translate([30, 18, 15])
        sphere(d=2);

        // Wing tips
        for(pos = [[20, 10, 20], [18, 8, 16]]) {
            translate(pos)
            sphere(d=1.5);
        }
    }

    // Red ink details (historical manuscript colors)
    color(color_red)
    union() {
        // Decorative spots on body
        for(i = [0:3]) {
            rotate([0, 0, 15 + i*10])
            translate([10 + i*3, 5 + i*2, 5 + i*4])
            sphere(d=1);
        }

        // Mouth line
        rotate([0, -20, 35])
        translate([6, 2.5, 23])
        cube([3, 0.5, 1]);

        // Claw details
        for(i = [0:1]) {
            rotate([0, 0, 45 + i*20])
            translate([20 + i*2, 10 + i, 0])
            cylinder(h=2, d=1.5);
        }
    }

    // Scrollwork decoration
    color(color_gold)
    for(i = [0:5]) {
        rotate([0, 0, i*60])
        translate([35, 15, -1])
        cylinder(h=1, d1=3, d2=1);
    }

    // Vine decoration curling around
    color(color_brown)
    for(t = [0:10:90]) {
        rotate([0, 0, t])
        translate([25 + cos(t*2)*5, 15 + sin(t*2)*5, 2 + t/20])
        sphere(d=1.5);
    }
}

// Scale to 3D printing size
scale([scale_factor, scale_factor, scale_factor])
marginalia_grotesque();

// Footnote Historical Artifact - Ancient Egyptian Papyrus Scroll Footnote
// A realistic style 3D printable footnote marker inspired by ancient Egyptian papyrus fragments
// Size: 60mm x 45mm x 8mm

$fn = 32;

// Main papyrus fragment base
module papyrus_fragment() {
    color([0.82, 0.71, 0.55])  // Papyrus color
    difference() {
        // Main papyrus body
        translate([0, 0, 1])
        cube([50, 35, 2], center=true);

        // Irregular torn edges
        union() {
            // Top tear
            translate([-20, 15, 0])
            scale([1, 0.5, 1])
            sphere(5);

            // Bottom tear
            translate([15, -15, 0])
            scale([1, 0.7, 1])
            sphere(4);

            // Side tears
            translate([22, 5, 0])
            scale([0.5, 1, 1])
            sphere(3);
        }
    }
}

// Hieroglyphic symbols as footnote markers
module hieroglyph_footnote() {
    color([0.3, 0.2, 0.1])
    translate([0, 0, 3])
    union() {
        // Ankh symbol (life)
        translate([-8, 0, 0])
        union() {
            // Main loop
            cylinder(h=1.5, r=3);
            // Cross bar
            translate([0, -3, 0.75])
            cube([6, 1.5, 1.5], center=true);
            // Vertical stem
            translate([0, 2.5, 0.75])
            cube([1.5, 5, 1.5], center=true);
        }

        // Eye of Horus
        translate([8, 0, 0])
        scale([0.8, 0.8, 1])
        union() {
            // Curved top
            translate([0, 1, 0.75])
            sphere(2, $fn=64);
            // Straight bottom
            translate([0, -1, 0.75])
            cube([4, 1, 1.5], center=true);
            // Markings
            translate([0, 0, 1.5])
            cube([0.5, 2.5, 0.5], center=true);
            translate([0, 0, 1.5])
            rotate([0, 0, 90])
            cube([0.5, 2.5, 0.5], center=true);
        }
    }
}

// Papyrus texture pattern (reed marks)
module papyrus_texture() {
    color([0.75, 0.65, 0.5])
    for(i = [-20:5:20]) {
        translate([i, 0, 2.1])
        cube([0.8, 35, 0.2]);
    }
    for(j = [-15:5:15]) {
        translate([0, j, 2.1])
        cube([50, 0.8, 0.2]);
    }
}

// Fragmented edge fibers
module edge_fibers() {
    color([0.78, 0.68, 0.53])
    for(i = [1:10]) {
        angle = i * 36;
        radius = 25 + sin(i * 3) * 3;
        translate([cos(angle) * radius, sin(angle) * radius, 1])
        rotate([0, 0, angle])
        scale([0.3, 2 + sin(i) * 1, 0.3])
        cylinder(h=1.5, r=0.5);
    }
}

// Ancient ink stains/decay
module ink_stains() {
    color([0.15, 0.12, 0.1])
    union() {
        // Main footnote area stain
        translate([0, 0, 2.2])
        scale([1.2, 0.8, 0.1])
        sphere(5, $fn=32);

        // Smaller stains
        translate([-12, 8, 2.2])
        scale([0.8, 0.6, 0.1])
        sphere(2, $fn=32);

        translate([10, -10, 2.2])
        scale([0.7, 0.5, 0.1])
        sphere(1.5, $fn=32);
    }
}

// Assembly
union() {
    papyrus_fragment();
    papyrus_texture();
    edge_fibers();
    hieroglyph_footnote();
    ink_stains();
}

// Historical Rubric - Medieval Manuscript Grading Tablet
// Based on 13th century wax tablets used by university masters

// Overall dimensions for 3D printing
tablet_width = 80;
tablet_height = 60;
tablet_thickness = 12;
wall_thickness = 3;

// Create the main tablet body with hollow interior
module tablet_body() {
    difference() {
        // Outer shell
        cube([tablet_width, tablet_height, tablet_thickness], center=true);

        // Hollow interior
        translate([0, 0, tablet_thickness/2 - wall_thickness])
            cube([tablet_width - 2*wall_thickness, tablet_height - 2*wall_thickness, 
                  tablet_thickness], center=true);

        // Finger hole for opening
        translate([0, -tablet_height/2 + 10, 0])
            rotate([90, 0, 0])
            cylinder(h=20, r=5, center=true);
    }
}

// Decorative border pattern
module decorative_border() {
    for(i = [0:3]) {
        rotate([0, 0, i*90])
        translate([tablet_width/2 - 5, tablet_height/2 - 5, 0])
        color("saddlebrown")
        for(j = [0:3]) {
            translate([0, -j*10, tablet_thickness/2])
            sphere(r=2);
        }
    }
}

// Wax writing surface
module wax_surface() {
    color("darkred")
    translate([0, 0, -tablet_thickness/2 + wall_thickness + 1])
    cube([tablet_width - 2*wall_thickness - 4, 
          tablet_height - 2*wall_thickness - 4, 
          2], center=true);
}

// Metal stylus holder loops
module stylus_holders() {
    color("goldenrod")
    for(pos = [-1, 1]) {
        translate([pos * (tablet_width/2 - 8), tablet_height/2 - 3, 0])
        rotate([90, 0, 0])
        difference() {
            cylinder(h=8, r=3, center=true);
            cylinder(h=9, r=2, center=true);
        }
    }
}

// Medieval inscription band
module inscription_band() {
    color("bronze")
    translate([0, 0, tablet_thickness/2 - 1])
    cube([tablet_width - 10, 8, 2], center=true);
}

// Latin inscription text (simulated with raised rectangles)
module inscription_text() {
    color("goldenrod")
    for(i = [0:7]) {
        translate([-30 + i*8, 0, tablet_thickness/2 + 0.5])
        cube([2, 1, 0.5], center=true);
    }
}

// Hinged clasp mechanism
module clasp() {
    color("silver")
    translate([0, tablet_height/2 - 2, 0])
    difference() {
        cube([20, 4, 6], center=true);
        translate([0, 0, 3])
            cube([16, 6, 4], center=true);
    }
}

// Assemble the complete rubric
union() {
    // Main tablet parts
    color("saddlebrown")
    tablet_body();

    // Surface and decorations
    wax_surface();
    decorative_border();
    inscription_band();
    inscription_text();
    stylus_holders();
    clasp();
}

// Add slight rounding to edges for realistic feel
module rounded_edges() {
    color("saddlebrown")
    translate([0, 0, 0])
    minkowski() {
        cube([tablet_width, tablet_height, tablet_thickness], center=true);
        sphere(r=1);
    }
}

// Illuminated Manuscript Book (Medieval)
// Overall dimensions: 70 x 50 x 8 mm

// Color definitions
cover_color = "saddlebrown";
gold_color = "goldenrod";
parchment_color = "wheat";

// Main module
module illuminated_book() {
    // Book cover
    color(cover_color) {
        // Front cover
        difference() {
            // Main cover body
            translate([0, 0, 4]) cube([70, 50, 2], center=true);

            // Central medallion recess
            translate([0, 0, 5]) 
                cube([25, 30, 2], center=true);
        }

        // Back cover
        translate([0, 0, -4]) mirror([0,0,1]) cube([70, 50, 2], center=true);
    }

    // Central gold illumination
    color(gold_color) {
        // Central medallion
        translate([0, 0, 5.5]) cylinder(h=0.5, d=20, $fn=32);

        // Decorative corners front
        for(x=[-25, 25])
            for(y=[-18, 18])
                translate([x, y, 5.5])
                    rotate([0, 0, 45])
                        cube([8, 8, 0.5], center=true);

        // Decorative corners back
        for(x=[-25, 25])
            for(y=[-18, 18])
                translate([x, y, -5.5])
                    rotate([0, 0, 45])
                        cube([8, 8, 0.5], center=true);
    }

    // Parchment pages
    color(parchment_color) {
        difference() {
            // Page block
            translate([0, 0, 0]) cube([64, 44, 6], center=true);

            // Text block recess
            translate([0, 0, 3.1]) cube([50, 35, 0.5], center=true);
        }
    }

    // Spine details
    color(cover_color) {
        // Spine ridges
        for(i=[-2, 0, 2]) {
            translate([35.5, i*10, 0])
                cube([3, 8, 10], center=true);
        }
    }

    // Gold decorative elements
    color(gold_color) {
        // Front cover border
        translate([0, 0, 5.5])
            difference() {
                cube([68, 48, 0.5], center=true);
                cube([60, 40, 1], center=true);
            }

        // Back cover border
        translate([0, 0, -5.5])
            difference() {
                cube([68, 48, 0.5], center=true);
                cube([60, 40, 1], center=true);
            }

        // Central jewel
        translate([0, 0, 6.2]) sphere(d=5);
    }

    // Illuminated letters on pages (simplified)
    color("darkred") {
        translate([-20, -15, 3.2]) cube([3, 5, 0.3]);
        translate([-16, -15, 3.2]) cube([3, 5, 0.3]);
        translate([-20, -8, 3.2]) cube([3, 5, 0.3]);
        translate([-16, -8, 3.2]) cube([3, 5, 0.3]);
    }
}

// Render the book
illuminated_book();

// Miniature Roman Oil Lamp (50-80 mm)
// Based on 1st century CE terracotta oil lamp designs

// Color definitions
color_terracotta = "#C65D00";

// Overall scaling factor
scale_factor = 0.8;

// Main lamp body module
module lamp_body() {
    // Body shell with hollow interior
    difference() {
        // Outer shell - elongated oval shape
        scale([1, 0.7, 0.4]) 
            sphere(d = 60);

        // Hollow interior
        translate([0, 0, -5])
            scale([0.85, 0.55, 0.3])
                sphere(d = 60);
    }
}

// Nozzle for wick
module nozzle() {
    // Main nozzle body
    translate([28, 0, 8])
        rotate([0, 20, 0])
        union() {
            // Tapered nozzle
            cylinder(h = 15, d1 = 12, d2 = 8);

            // Wick hole (vertical cylinder)
            translate([0, 0, 15])
                cylinder(h = 5, d = 4);
        }

    // Fill hole on top
    translate([0, 0, 12])
        cylinder(h = 5, d = 12);
}

// Decorative handle ring
module handle() {
    translate([-25, 0, 10])
        rotate([0, 90, 0])
        difference() {
            // Handle ring
            torus(8, 2, $fn=32);

            // Attachment gap
            rotate([0, 90, 0])
                cube([20, 10, 10], center=true);
        }
}

// Decorative pattern stamps around the edge
module decoration() {
    // Groove around the edge
    difference() {
        scale([1, 0.7, 0.42]) 
            sphere(d = 62);
        scale([0.95, 0.65, 0.43]) 
            sphere(d = 62);
    }

    // Small decorative dots
    for(i = [0:15]) {
        rotate([0, 0, i * 22.5])
            translate([25, 0, 12])
                sphere(d = 2);
    }
}

// Base ring for stability
module base_ring() {
    difference() {
        // Outer ring
        cylinder(h = 4, d = 45);

        // Inner hollow
        cylinder(h = 5, d = 35);
    }
}

// Complete lamp assembly
color(color_terracotta)
scale([scale_factor, scale_factor, scale_factor])
union() {
    // Main components
    lamp_body();
    nozzle();
    handle();
    decoration();

    // Base
    translate([0, 0, -2])
        base_ring();
}

// Egyptian Canopic Jar of Hapi (Son of Horus)
// Medium complexity realistic model - 3D printable without supports
// Overall height ~80 mm, base ~40 mm diameter

// Colors
$fn = 128;

// Main jar body
module jar_body() {
    difference() {
        union() {
            // Main cylindrical body
            cylinder(h = 55, d = 40, center = false);

            // Slight bulge in middle for realistic shape
            translate([0, 0, 27.5])
                scale([1.05, 1.05, 1])
                cylinder(h = 20, d = 40, center = true);

            // Base disk
            translate([0, 0, -2])
                cylinder(h = 4, d = 42, center = false);
        }

        // Hollow interior - wall thickness 3mm
        translate([0, 0, 2])
            cylinder(h = 51, d = 34, center = false);
    }
}

// Lid with baboon head (Hapi)
module baboon_head_lid() {
    union() {
        // Lid base
        translate([0, 0, 55])
            cylinder(h = 8, d = 38, center = false);

        // Neck transition
        translate([0, 0, 63])
            cylinder(h = 5, d = 30, center = false);

        // Baboon head base
        translate([0, 0, 68])
            sphere(d = 28);

        // Muzzle
        translate([0, -8, 68])
            scale([0.8, 1.2, 0.9])
            sphere(d = 18);

        // Nose
        translate([0, -12, 70])
            sphere(d = 6);

        // Eyes
        for(x = [-6, 6]) {
            translate([x, -2, 75])
                sphere(d = 4);
        }

        // Ears
        for(x = [-12, 12]) {
            translate([x, 0, 78])
                rotate([0, 90, 0])
                cylinder(h = 8, d1 = 6, d2 = 0, center = true);
        }

        // Decorative collar
        translate([0, 0, 63])
