// Roman Limes Border Stone (Limitanei Milestone)
// Typical 2nd-3rd century AD frontier marker with Latin inscription
// Found along Danube Limes frontier defenses

// Overall dimensions for 3D printing
stone_height = 70;
base_width = 35;
top_width = 30;
thickness = 15;

// Colors
stone_color = [0.7, 0.65, 0.6];  // Weathered limestone
bronze_color = [0.7, 0.5, 0.3];  // Bronze plaque

module base_stone() {
    difference() {
        // Main tapered stone body
        translate([0, 0, stone_height/2])
            scale([1, 1, stone_height])
                cylinder(h=1, r1=base_width/2, r2=top_width/2, $fn=6);

        // Carved inscription panel (front face)
        translate([0, thickness/2 + 0.1, stone_height * 0.6])
            cube([top_width * 0.8, 2, stone_height * 0.3], center=true);
    }
}

module bronze_plaque() {
    color(bronze_color) {
        // Bronze information plaque
        translate([0, thickness/2 + 1, stone_height * 0.6])
            cube([top_width * 0.7, 0.5, stone_height * 0.25], center=true);

        // Mounting rivets (4 corners)
        for(x = [-1, 1])
            for(z = [-1, 1])
                translate([x * top_width * 0.3, thickness/2 + 1.5, stone_height * 0.6 + z * stone_height * 0.1])
                    cylinder(h=0.5, r=1, $fn=8);
    }
}

module border_markings() {
    // M P XXII (Miliarium Passuum 22 - 22nd mile marker)
    translate([0, thickness/2 + 1.3, stone_height * 0.7])
        scale([0.1, 0.1, 0.1])
            linear_extrude(height=0.5)
                text("M P XXII", size=8, halign="center", valign="center", font="Liberation Sans:style=Bold");

    // Legion mark (LEG XXI)
    translate([0, thickness/2 + 1.3, stone_height * 0.55])
        scale([0.08, 0.08, 0.08])
            linear_extrude(height=0.5)
                text("LEG XXI RAPAX", size=6, halign="center", valign="center", font="Liberation Sans:style=Bold");
}

module weathering_details() {
    // Surface chips and erosion
    for(i = [1:8]) {
        rotate([0, 0, i * 45])
            translate([base_width/2 * 0.8, 0, i * stone_height/8])
                sphere(r=1 + i/4);
    }

    // Moss/lichen patches
    color([0.4, 0.5, 0.3]) {
        for(i = [1:5]) {
            rotate([0, 0, i * 72])
                translate([base_width/2 * 0.7, thickness/4, i * stone_height/5])
                    sphere(r=2);
        }
    }
}

module roman_border_stone() {
    color(stone_color) {
        difference() {
            union() {
