        translate([0, 0, -0.5])
        cube([76, 96, 6], center = true);
    }

    // Corner bosses (4 corners)
    module corner_boss(x, y) {
        translate([x, y, 3])
        difference() {
            cylinder(h = 3, r = 6, center = true);
            translate([0, 0, 1])
            cylinder(h = 4, r = 4, center = true);
        }
    }

    color(color_gold)
    corner_boss(35, 45);
    corner_boss(-35, 45);
    corner_boss(35, -45);
