    color(color_gold)
    translate([0, 0, 4])
    scale([0.6, 0.8, 1])
    cube([20, 25, 1], center = true);

    // Vertical decorative ribs
    module rib(x) {
        color(color_gold)
        translate([x, 0, 2])
        cube([2, 90, 1], center = true);
    }

    for (i = [-1:1]) {
        rib(i * 25);
    }

    // Horizontal decorative bands
    module band(y) {
        color(color_gold)
        translate([0, y, 2])
        cube([70, 2, 1], center = true);
    }

    band(30);
    band(0);
    band(-30);

    // Rosette stamps in border
    module rosette(x, y) {
        color(color_gold)
        translate([x, y, 3])
        difference() {
            cylinder(h = 1, r = 4, center = true);
            for (i = [0:5]) {
