    rosette(38, 20);
    rosette(38, -20);
    rosette(-38, 20);
    rosette(-38, -20);

    // Title inscription area
    color([0.9, 0.9, 0.9])
    translate([0, 35, 3.5])
    cube([50, 8, 0.5], center = true);
