// Colophon Historical Artifact - Babylonian Clay Tablet
// Realistic style with cuneiform inscription impression and seal markings

$fn = 50;

// Overall dimensions
tablet_width = 80;
tablet_height = 60;
tablet_thickness = 12;

// Color definitions
clay_color = "#8B4513";
inscription_color = "#654321";
seal_color = "#CD853F";

// Main tablet body
module tablet_body() {
    color(clay_color) {
        difference() {
            // Main tablet
            cube([tablet_width, tablet_height, tablet_thickness], center=true);

            // Rounded edges
            translate([0, 0, tablet_thickness/2 - 1]) {
                cylinder(h=tablet_thickness + 2, r=3, center=true);
            }
        }
    }
}

// Cuneiform inscription pattern
module cuneiform_inscription() {
    color(inscription_color) {
        // Front face inscription
        translate([0, 0, tablet_thickness/2 + 0.1]) {
            for(x = [-35:10:35]) {
                for(y = [-25:8:25]) {
                    if((x + y) % 20 == 0) {
                        translate([x, y, 0]) {
                            // Wedge-shaped cuneiform marks
                            rotate([0, 0, 45]) {
                                cube([2, 0.5, 0.3], center=true);
                            }
                        }
                    }
                }
            }
        }

        // Back face inscription
        translate([0, 0, -tablet_thickness/2 - 0.1]) {
            rotate([180, 0, 0]) {
                for(x = [-30:12:30]) {
                    for(y = [-20:10:20]) {
                        if((x + y) % 15 == 0) {
                            translate([x, y, 0]) {
                                // Horizontal wedge marks
                                cube([3, 0.8, 0.2], center=true);
                            }
                        }
                    }
                }
            }
        }
    }
}

// Cylinder seal impression
module cylinder_seal() {
    color(seal_color) {
        // Main seal impression
        translate([0, tablet_height/2 - 8, tablet_thickness/2 - 1]) {
            difference() {
                cylinder(h=2, r=8, center=false);
                // Decorative pattern on seal
                for(i = [0:30:330]) {
                    rotate([0, 0, i]) {
                        translate([5, 0, -0.5]) {
                            cube([1, 0.5, 2.5], center=true);
                        }
                    }
                }
            }
        }

        // Secondary seal on back
        translate([0, -tablet_height/2 + 10, -tablet_thickness/2 + 1]) {
            difference() {
                cylinder(h=2, r=6, center=false);
                // Cross pattern
                for(i = [0:90:270]) {
                    rotate([0, 0, i]) {
                        translate([3, 0, -0.5]) {
                            cube([4, 1, 2.5], center=true);
                        }
                    }
                }
            }
        }
    }
}

// Edge decorations
module edge_decorations() {
    color(clay_color) {
        // Top edge rope pattern
        translate([0, tablet_height/2, 0]) {
            for(x = [-tablet_width/2 + 5:5:tablet_width/2 - 5]) {
                translate([x, 0, 0]) {
                    sphere(r=1, center=true);
                }
            }
        }

        // Bottom edge rope pattern
        translate([0, -tablet_height/2, 0]) {
            for(x = [-tablet_width/2 + 7:5:tablet_width/2 - 7]) {
                translate([x, 0, 0]) {
                    sphere(r=1, center=true);
                }
            }
        }
    }
}

// Maker's mark on side
module makers_mark() {
    color(inscription_color) {
        translate([tablet_width/2 - 2, 0, 0]) {
            rotate([0, 90, 0]) {
                for(i = [-2:1:2]) {
                    translate([0, i*2, 0]) {
                        cube([0.5, 1, 0.5], center=true);
                    }
                }
            }
        }
    }
}

// Assembly
union() {
    tablet_body();
    cuneiform_inscription();
    cylinder_seal();
    edge_decorations();
    makers_mark();
}

// Babylonian Cuneiform Clay Tablet Imprint (ca. 1800 BCE)
// Realistic style, medium complexity, 3D-printable

// Overall tablet dimensions (scaled for printing)
tablet_w = 70;
tablet_h = 50;
tablet_d = 12;
wall_t = 2.5;          // safe printable wall thickness
corner_r = 3;          // small fillet for printability

module rounded_cube(w, h, d, r) {
    // Create a cube with slightly rounded edges for printability
    translate([r, r, 0])
    minkowski() {
        cube([w-2*r, h-2*r, d]);
        sphere(r);
    }
}

module cuneiform_lines() {
    // Stylised wedge impressions running left-to-right
    color("Brown")
    for(y = [-15:10:15]) {
        translate([0, y, 0])
        for(x = [-25:6:25]) {
            translate([x, 0, -0.8])
            rotate([0, 0, -30])
            scale([1, 0.5, 1])
            cylinder(h=1.5, d1=2.5, d2=1.5, $fn=8);
        }
    }
}

module numerical_tablet() {
    // Main tablet body
    difference() {
        // Outer rounded tablet
        rounded_cube(tablet_w, tablet_h, tablet_d, corner_r);

        // Hollow interior for imprinting
        translate([wall_t, wall_t, -tablet_d])
        cube([tablet_w-2*wall_t, tablet_h-2*wall_t, tablet_d]);
    }
}

module seal_imprint() {
    // Central circular seal impression
    color("Sienna")
    difference() {
        // Raised ring
        translate([0, 0, tablet_d-1])
        cylinder(h=1.5, r=10, $fn=32);

        // Central recess
        translate([0, 0, tablet_d-1.8])
        cylinder(h=2, r=8, $fn=32);
    }

    // Eight-point star inside seal
    color("Brown")
    for(a = [0:45:360-45]) {
        rotate([0, 0, a])
        translate([0, 4.5, tablet_d-1.8])
        scale([0.8, 0.8, 1])
        cube([1.5, 9, 1.5], center=true);
    }
}

module edge_frame() {
    // Decorative raised frame around upper edge
    color("SaddleBrown")
    translate([0, 0, tablet_d-1])
    difference() {
        rounded_cube(tablet_w-4, tablet_h-4, 1.5, corner_r-1);
        translate([1.5, 1.5, -1])
        rounded_cube(tablet_w-7, tablet_h-7, 3, corner_r-2);
    }
}

// Assembly
union() {
    numerical_tablet();
    edge_frame();
    translate([0, 0, 0]) seal_imprint();
    translate([0, 0, tablet_d-1.5]) cuneiform_lines();
}

