// Medieval Signet Ring Seal (13th Century Style)
// Overall dimensions: 80mm tall ring with 25mm diameter seal face

// Bronze color for historical accuracy
color([0.72, 0.45, 0.2]) {

// Ring shank - split for 3D printing
module ring_shank() {
    difference() {
        // Main ring body
        rotate([0, 90, 0]) 
            cylinder(h=5, d1=25, d2=25, $fn=100);

        // Hollow center for finger
        rotate([0, 90, 0]) 
            cylinder(h=7, d1=18, d2=18, $fn=100);

        // Split gap for printing
        translate([0, -15, -3])
            cube([30, 30, 6]);
    }
}

// Decorative bezel collar
module bezel_collar() {
    difference() {
        // Main collar
        cylinder(h=8, d1=30, d2=28, $fn=6);

        // Hollow center
        cylinder(h=9, d1=20, d2=20, $fn=100);
    }
}

// Heraldic lion rampant seal face
module seal_face() {
    // Seal base
    cylinder(h=3, d=25, $fn=100);

    // Heraldic shield shape
    translate([0, 0, 3])
    scale([1, 1.3, 1])
    cylinder(h=1, d=20, $fn=3);

    // Lion rampant body (simplified)
    translate([0, 5, 3.5])
    rotate([0, 0, 45])
    cube([3, 8, 1.5], center=true);

    // Lion head
    translate([0, 2, 3.5])
    sphere(d=4);

    // Lion tail
    translate([0, 8, 3.5])
    rotate([0, 0, -30])
    cylinder(h=6, d1=2, d2=0.5, $fn=8);

    // Cross hatching background
    for(i=[-10:2:10]) {
        translate([i, 0, 3.2])
        cube([0.5, 20, 0.2]);
    }
    for(i=[-10:2:10]) {
        translate([0, i, 3.2])
        cube([20, 0.5, 0.2]);
    }
}

// Decorative ring patterns
module ring_patterns() {
    // Raised nubs pattern
    for(i=[0:30:330]) {
        rotate([0, 0, i])
        translate([11.5, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=1, d=2, $fn=16);
    }
}

// Assembly
