// Roman Lead Matrix Seal - Historical Artifact
// Used for authenticating documents and correspondence in ancient Rome

$fn = 32;

// Main dimensions
matrix_diameter = 45;
matrix_height = 12;
handle_height = 25;
handle_diameter = 12;

// Colors
color_lead = [0.4, 0.4, 0.45];
color_patina = [0.3, 0.5, 0.4, 0.7];

// Main module
module roman_matrix_seal() {
    // Main seal disk
    color(color_lead)
    union() {
        // Base disk with slight taper
        cylinder(h = matrix_height, d1 = matrix_diameter, d2 = matrix_diameter * 0.95);

        // Handle
        translate([0, 0, matrix_height])
        cylinder(h = handle_height, d = handle_diameter);

        // Handle grip ring
        translate([0, 0, matrix_height + handle_height - 3])
        cylinder(h = 6, d = handle_diameter + 4);

        // Decorative rings on handle
        for(i = [1:3]) {
            translate([0, 0, matrix_height + i * 6])
            cylinder(h = 1, d = handle_diameter + 2);
        }
    }

    // Intaglio design on bottom - SPQR inscription
    translate([0, 0, -0.2])
    difference() {
        // Base of seal bottom
        cylinder(h = 2, d = matrix_diameter * 0.9);

        // SPQR inscription (Senatus Populusque Romanus)
        // S
        translate([-12, 5, -1])
        cube([3, 10, 2]);
        translate([-15, 5, -1])
        cube([6, 3, 2]);
        translate([-15, 12, -1])
        cube([6, 3, 2]);

        // P
        translate([-5, 5, -1])
        cube([3, 10, 2]);
        translate([-5, 12, -1])
        cube([6, 3, 2]);

        // Q
        circle(d = 12);
        translate([4, -4, -1])
        cube([3, 8, 2]);

        // R
        translate([10, 5, -1])
        cube([3, 10, 2]);
        translate([10, 12, -1])
        cube([6, 3, 2]);
        translate([10, 8, -1])
        cube([6, 5, 2]);
        translate([13, 5, -1])
        cube([3, 5, 2]);
    }

    // Decorative border pattern
    translate([0, 0, matrix_height - 2])
    difference() {
        cylinder(h = 2, d = matrix_diameter + 2);

        // Repeating dot pattern
        for(i = [0:35]) {
            rotate([0, 0, i * 10])
            translate([matrix_diameter/2 - 1, 0, -1])
            cylinder(h = 4, d = 3);
        }
    }

    // Age patina effect
    color(color_patina)
    translate([0, 0, -0.1])
    cylinder(h = matrix_height + handle_height + 10, d = matrix_diameter + 2);
}

// Create the matrix seal
roman_matrix_seal();

// Bronze bull figurine inspired by Minoan art (ca. 1600 BCE)
// Overall size ~80 mm long, 60 mm high, 40 mm wide

$fn = 48;  // Smooth curves for printing

// Main body
color([0.4,0.2,0.05])
union(){
    // Torso  slightly elongated for the Minoan style
    hull(){
        translate([-15,-15,0]) sphere(12);
        translate([25,-15,0]) sphere(12);
        translate([-15,15,0]) sphere(12);
        translate([25,15,0]) sphere(12);
    }

    // Neck
    translate([25,0,10]) rotate([0,-20,0])
        cylinder(h=20, r1=10, r2=6, center=true);

    // Head
    translate([38,-1,23]) cube([22,18,18], center=true);

    // Hump on shoulders (typical of Minoan bulls)
    translate([12,0,18]) sphere(10);

    // Tail
    translate([-28,0,12]) rotate([0,20,0])
        cylinder(h=20, r1=3, r2=2);

    // Legs  thick for stability and printing without support
    module leg(dx){
        translate([dx,-12,0]) rotate([0,15,0])
            cylinder(h=22, r1=4, r2=5, center=false);
    }
    leg(15); leg(-5);
    mirror([0,1,0]){
        leg(15); leg(-5);
    }

    // Horns  curved forward in the Minoan tradition
    module horn(tx){
        color([0.85,0.7,0.4])
        translate([tx,8,30]) rotate([0,0,30]) rotate([0,90,0])
            cylinder(h=12, r1=2, r2=4);
    }
    horn(45); horn(45) mirror([0,1,0]);

    // Eyes
    color("black")
    translate([46,-6,27]) sphere(2);
    translate([46,6,27]) sphere(2);

    // Decorative rosette on the forehead
    color([0.6,0.3,0.1])
    translate([46,0,33]) cylinder(h=1, r=4, $fn=8);
    for(a=[0:60:300])
        rotate([0,0,a])
            translate([46,3.5,33.5]) cube([1,3,0.5], center=true);
}

