// OpenSCAD model: Roman wax-tablet writing tablet (tabula)
// Historical appeal tablet with stylus holder
// Overall size ~90 mm x 60 mm x 15 mm

// Basic parameters
tabletW = 85;
tabletH = 55;
tabletD = 8;
hingeR = 2.5;
stylusL = 75;
stylusR = 3;
gap = 0.4;

// Colors
colorWood = [139/255, 69/255, 19/255];
colorWax = [90/255, 60/255, 40/255];

// Main tablet body with wax recess
module tabletBody() {
    // Outer wooden shell
    color(colorWood)
    difference() {
        // Main block
        cube([tabletW, tabletH, tabletD], center=true);

        // Recess for wax on front
        translate([0, 0, tabletD/2 - 1])
            cube([tabletW - 6, tabletH - 6, 2.5], center=true);

        // Groove for stylus
        translate([tabletW/2 - 8, 0, tabletD/2])
            rotate([90, 0, 0])
                cylinder(h=tabletH - 10, r=stylusR + 1, center=true);
    }

    // Wax layer
    color(colorWax)
    translate([0, 0, tabletD/2 - 1.2])
        cube([tabletW - 7, tabletH - 7, 1], center=true);
}

// Stylus
module stylus() {
    // Main shaft
    color([0.8, 0.8, 0.8])
    union() {
        cylinder(h=stylusL, r=stylusR, center=true);
        // Pointed end
        translate([stylusL/2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=stylusR*2, r1=stylusR, r2=0, center=true);
        // Flat end for erasing
        translate([-stylusL/2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=stylusR*2, r1=stylusR, r2=stylusR*1.5, center=true);
    }
}

// Decorative metal corners
module metalCorner() {
    color([0.7, 0.7, 0.7])
    difference() {
        cube([8, 8, 2], center=true);
        cylinder(h=3, r=1.5, center=true);
    }
}

// Assembly
translate([0, 0, tabletD/2])
    tabletBody();

// Place corners
for(x = [-1, 1]) {
    for(y = [-1, 1]) {
        translate([x * (tabletW/2 - 4), y * (tabletH/2 - 4), tabletD + 0.5])
            metalCorner();
    }
}

// Stylus in groove
translate([tabletW/2 - 8, 0, tabletD/2 + 1])
    rotate([90, 0, 0])
        stylus();

