// Rosetta Stone - Key to understanding hieroglyphs (196 BCE)
// Historical replica with accurate proportions and inscriptions

// Color definitions
stone_color = [0.75, 0.73, 0.68];
shadow_color = [0.5, 0.48, 0.45];

// Main dimensions (real stone: 1144mm x 723mm x 279mm)
// Scaled to 100mm height for 3D printing
scale_factor = 100 / 1144;
width = 723 * scale_factor;
height = 1144 * scale_factor;
thickness = 279 * scale_factor;

// Wall thickness for 3D printing
wall_thick = 2;

module rounded_cube(size, radius) {
    hull() {
        translate([radius, radius, radius]) sphere(r = radius);
        translate([size.x - radius, radius, radius]) sphere(r = radius);
        translate([radius, size.y - radius, radius]) sphere(r = radius);
        translate([size.x - radius, size.y - radius, radius]) sphere(r = radius);
        translate([radius, radius, size.z - radius]) sphere(r = radius);
        translate([size.x - radius, radius, size.z - radius]) sphere(r = radius);
        translate([radius, size.y - radius, size.z - radius]) sphere(r = radius);
        translate([size.x - radius, size.y - radius, size.z - radius]) sphere(r = radius);
    }
}

module hieroglyph_line(y_pos, line_length) {
    // Simulate hieroglyph text with small cubes
    for (i = [0:3:line_length]) {
        translate([i, y_pos, -thickness/2 + 1])
            cube([1.5, 2, 0.5]);
    }
}

module demotic_line(y_pos, line_length) {
    // Simulate demotic script with elongated rectangles
    for (i = [0:2:line_length]) {
        translate([i, y_pos, -thickness/2 + 0.5])
            cube([2, 1.5, 0.3]);
    }
}

module greek_line(y_pos, line_length) {
    // Simulate Greek text with small cylinders
    for (i = [0:3:line_length]) {
        translate([i, y_pos, -thickness/2 + 0.8])
            cylinder(h = 0.8, r = 0.8);
    }
}

// Main stone body
color(stone_color) {
    difference() {
        // Outer rounded stone
        rounded_cube([width, height, thickness], 3);

        // Hollow center for 3D printing
        translate([wall_thick, wall_thick, wall_thick])
            rounded_cube([width - 2*wall_thick, height - 2*wall_thick, thickness - wall_thick], 2);
    }
}

// Top broken edge simulation
color(shadow_color) {
    translate([width*0.7, height*0.98, 0])
        rotate([0, 15, 0])
        cube([width*0.3, thickness*0.5, thickness]);
}

// Hieroglyphic section (top 1/3)
for (j = [height*0.65:6:height*0.95]) {
    hieroglyph_line(j, width*0.9);
}

// Demotic section (middle 1/3)
for (j = [height*0.35:4:height*0.6]) {
    demotic_line(j, width*0.85);
}

// Greek section (bottom 1/3)
for (j = [height*0.05:5:height*0.3]) {
    greek_line(j, width*0.8);
}

// Corner damage details
color(shadow_color) {
    translate([width - 8, 5, thickness - 3])
        sphere(r = 6);

    translate([5, height - 8, thickness - 3])
        sphere(r = 5);
}

// Surface texture for ancient stone
module surface_pit(x, y, size) {
    translate([x, y, -thickness/2 + 0.1])
        sphere(r = size);
}

// Add random surface pits for realism
for (i = [0:15]) {
    color(shadow_color) {
        surface_pit(rands(5, width-5, 1)[0], 
                   rands(5, height-5, 1)[0], 
                   rands(0.5, 1.5, 1)[0]);
    }
}

// Small chips along edges
module edge_chip(pos, rot) {
    translate(pos)
        rotate(rot)
        cube([3, 2, 1]);
}

color(shadow_color) {
    edge_chip([width*0.2, 0, thickness*0.3], [0, 0, 30]);
    edge_chip([width*0.8, height, thickness*0.7], [0, 0, -20]);
    edge_chip([0, height*0.4, thickness*0.5], [0, 0, 45]);
}

