edge_chamfer = 2;

// Cuneiform wedge parameters
wedge_w = 2.2;
wedge_h = 1.8;
wedge_d = 1.2;
rows = 6;
cols_per_row = [10,12,9,11,8,10];

module wedge() {
    // Stylized triangular cuneiform impression
    difference() {
        cube([wedge_w, wedge_d, wedge_h], center=true);
        translate([0,0,0.3])
            cube([wedge_w*0.6, wedge_d*1.1, wedge_h], center=true);
    }
}

module line_of_wedges(count, y_offset) {
    for (i = [0:count-1]) {
        x_pos = -tablet_w/2.4 + i*(tablet_w*0.8/count);
        translate([x_pos, y_offset, -tablet_d/2 + wedge_h/2 + 0.2])
            wedge();
    }
}

module cuneiform_text() {
    for (r = [0:rows-1]) {
        y_pos = -tablet_h/2.5 + r*(tablet_h*0.8/rows);
        line_of_wedges(cols_per_row[r], y_pos);
    }
}

module tablet_body() {
    // Main tablet with chamfered edges
    difference() {
        // Base tablet
        translate([0,0,0])
            cube([tablet_w, tablet_h, tablet_d], center=true);

        // Top chamfer
        translate([0,0,tablet_d/2-edge_chamfer])
            cube([tablet_w+2, tablet_h+2, edge_chamfer*2], center=true);

        // Bottom chamfer
        translate([0,0,-tablet_d/2+edge_chamfer])
            cube([tablet_w+2, tablet_h+2, edge_chamfer*2], center=true);
    }
}

module edge_cracks() {
    // Random weathering cracks for realism
    crack_positions = [
        [-25, 18, 0],
        [20, -15, 0],
        [-10, -20, 0],
        [30, 10, 0]
    ];

    for (pos = crack_positions) {
        translate(pos)
            rotate([0,0,rands(0,360,1)[0]])
            scale([1, rands(0.5,1.5,1)[0], 1])
                cylinder(h=tablet_d*0.8, r1=0.8, r2=0.2, center=true);
    }
}

module seal_impression() {
    // Cylinder seal roll mark on edge
    translate([tablet_w/2-2, 0, 0])
        rotate([90,0,0])
            cylinder(h=3, r=8, center=true);

    // Seal pattern dots
    for (i = [0:7]) {
        angle = i*45;
        translate([tablet_w/2-2, 0, 0])
            rotate([90,0,angle])
                translate([0,6,0])
                    sphere(d=1.5);
    }
}

// Main assembly
color("burlywood")
union() {
    tablet_body();

    // Cuneiform writing on front face
    translate([0,0,tablet_d/2+0.01])
        cuneiform_text();

    // Additional text on back
    translate([0,0,-tablet_d/2-0.01])
        rotate([180,0,0])
            cuneiform_text();

    // Weathering details
    edge_cracks();

    // Seal impression
    seal_impression();
}

// Rosetta Stone Replica - Historical Artifact
// Dimensions: 70mm x 50mm x 20mm

$fn = 50;

// Main stone slab
module rosetta_stone() {
    color("darkslategray")
    difference() {
        // Main body with rounded edges
        union() {
            translate([-35, -25, 0])
                cube([70, 50, 20], center = false);

            // Rounded top edge
            translate([0, 25, 10])
                rotate([0, 90, 0])
                    cylinder(h = 70, r = 10);

            // Rounded bottom edge
            translate([0, -25, 10])
                rotate([0, 90, 0])
                    cylinder(h = 70, r = 10);
        }

        // Rounded corners
        translate([35, 25, 10])
            sphere(r = 10);
        translate([-35, 25, 10])
            sphere(r = 10);
        translate([35, -25, 10])
            sphere(r = 10);
        translate([-35, -25, 10])
            sphere(r = 10);
    }
}

// Carved inscription bands
module inscription_band(x_pos, width) {
    color("dimgray")
    translate([x_pos, -25, 1])
        cube([width, 50, 0.5]);
}

// Hieroglyphic symbols (simplified)
module hieroglyph(x, y) {
    color("black")
    translate([x, y, 2])
        rotate([0, 0, 45])
            cube([2, 2, 0.5], center = true);
}

// Greek text symbols (simplified)
module greek_text(x, y) {
    color("black")
    translate([x, y, 2])
        cylinder(h = 0.3, r = 1);
}

// Demotic text symbols (simplified)
module demotic_text(x, y) {
    color("black")
    translate([x, y, 2])
        cube([3, 1, 0.3], center = true);
}

// Main assembly
union() {
    // Stone base
    rosetta_stone();

    // Three distinct text bands
    inscription_band(-30, 20);  // Hieroglyphic section
    inscription_band(-10, 20);  // Demotic section
    inscription_band(10, 20);   // Greek section

    // Hieroglyphic details (top section)
    for (i = [-25:5:-15]) {
        for (j = [-20:5:20]) {
            if ((i + j) % 2 == 0)
                hieroglyph(i, j);
        }
    }

    // Demotic details (middle section)
    for (i = [-5:3:5]) {
        for (j = [-20:5:20]) {
            if (j % 3 == 0)
                demotic_text(i, j);
        }
    }

    // Greek details (bottom section)
    for (i = [15:3:25]) {
        for (j = [-20:5:20]) {
            if ((i + j) % 2 == 1)
                greek_text(i, j);
        }
    }

    // Weathering cracks (realistic detail)
    color("gray")
    translate([0, 0, 19])
        union() {
            // Main diagonal crack
            translate([10, -15, 0])
                rotate([0, 0, 30])
                    cube([25, 0.5, 1]);

            // Secondary cracks
            translate([-10, 5, 0])
                rotate([0, 0, -20])
                    cube([20, 0.3, 1]);

            translate([-20, -10, 0])
                rotate([0, 0, 45])
                    cube([15, 0.3, 1]);
        }
}

// Scale to 3D printing size
