// Roman bronze wax-tablet deposition stylus (Tabula ansata stylus)
// Typical length 150-180 mm, here scaled to 70 mm for printing
// Used to inscribe wax tablets in legal depositions and contracts

module stylus_body() {
    // Main shaft - octagonal cross-section for better grip
    linear_extrude(height=50, center=true, convexity=10)
        offset(r=2)
            difference() {
                circle(d=8, $fn=8);
                circle(d=6, $fn=8);
            }
}

module pointed_end() {
    // Sharp writing point
    translate([0, 0, -25])
        cylinder(h=10, r1=4, r2=0.5, $fn=8);
}

module flat_spatula() {
    // Flat end for erasing wax
    translate([0, 0, 25])
        cube([12, 3, 3], center=true);
}

module decorative_ring() {
    // Bronze ring decoration near center
    for(i=[-1, 1]) {
        translate([0, 0, i*15])
            rotate([90, 0, 0])
                cylinder(h=2, r1=5, r2=5, $fn=32);
    }
}

module inscription_band() {
    // Faint inscription band with maker's mark
    translate([0, 0, 0])
        rotate([90, 0, 0])
            difference() {
                cylinder(h=1.5, r1=4.2, r2=4.2, $fn=64);
                // Tiny dots for inscription texture
                for(i=[0:15]) {
                    rotate([0, 0, i*22.5])
                        translate([3.8, 0, 0])
                            sphere(d=0.3, $fn=8);
                }
            }
}

module grip_texture() {
    // Diagonal grip lines for better handling
    for(i=[-20:5:20]) {
        rotate([0, 90, 45])
            translate([i, 0, 0])
                cube([1, 2, 8], center=true);
    }
}

// Main assembly
color("bronze") {
    union() {
        stylus_body();
        pointed_end();
        flat_spatula();
        decorative_ring();
        inscription_band();
        grip_texture();
    }
}

