// Historical Affidavit Seal with Wax Imprint
// Represents a 19th century legal document with wax seal

// Set overall scale for 3D printing
scale_factor = 1;

// Document dimensions (mm)
doc_width = 60 * scale_factor;
doc_height = 80 * scale_factor;
doc_thickness = 0.8 * scale_factor;

// Wax seal dimensions
seal_diameter = 18 * scale_factor;
seal_height = 4 * scale_factor;

// Ribbon dimensions
ribbon_width = 3 * scale_factor;
ribbon_thickness = 0.4 * scale_factor;

// Paper texture module using small cubes
module paper_texture() {
    difference() {
        color([0.95, 0.93, 0.88])  // Parchment color
        cube([doc_width, doc_height, doc_thickness], center = true);

        // Create subtle paper texture with small indentations
        for (x = [-doc_width/2 + 2 : 2 : doc_width/2 - 2]) {
            for (y = [-doc_height/2 + 2 : 2 : doc_height/2 - 2]) {
                if (rands(0, 1, 1)[0] > 0.7) {
                    translate([x, y, doc_thickness/2 - 0.05])
                    cube([0.5, 0.5, 0.1], center = true);
                }
            }
        }
    }
}

// Wax seal module
module wax_seal() {
    // Wax base
    color([0.6, 0.1, 0.1])  // Deep red wax color
    union() {
        cylinder(h = seal_height, d = seal_diameter, $fn = 32);

        // Decorative seal impression (shield shape)
        translate([0, 0, seal_height - 0.5])
        scale([1, 1.2, 1])
        cylinder(h = 0.5, d = seal_diameter * 0.8, $fn = 3);

        // Central star symbol
        translate([0, 0, seal_height - 0.3])
        scale([1, 1, 0.2])
        sphere(d = seal_diameter * 0.4, $fn = 8);
    }
}

// Legal text lines
module text_lines() {
    color([0.2, 0.2, 0.2])  // Dark ink color
    for (i = [1 : 6]) {
        translate([0, -doc_height/2 + 10 + i * 8, doc_thickness/2 + 0.1])
        cube([doc_width * 0.8, 0.5, 0.1], center = true);
    }

    // Signature line
    translate([doc_width/4, doc_height/2 - 15, doc_thickness/2 + 0.1])
    cube([doc_width * 0.4, 0.5, 0.1], center = true);
}

// Ribbon module
module ribbon() {
    color([0.8, 0.1, 0.1])  // Red ribbon color
    union() {
        // Main ribbon
        translate([doc_width/2 - 5, 0, 0])
        cube([ribbon_width, doc_height * 0.6, ribbon_thickness]);

        // Folded part
        translate([doc_width/2 - 5 - ribbon_width/2, doc_height/4, -ribbon_thickness])
        rotate([90, 0, 0])
        cylinder(h = ribbon_width, r = ribbon_thickness, $fn = 16);
    }
}

// Corner decorative elements
module corner_ornaments() {
    color([0.3, 0.2, 0.1])  // Dark brown ink
    for (corner = [[-1, -1], [1, -1], [-1, 1], [1, 1]]) {
        translate([corner[0] * (doc_width/2 - 3), corner[1] * (doc_height/2 - 3), doc_thickness/2 + 0.1])
        rotate([0, 0, corner[0] * corner[1] * 45])
        union() {
            cube([2, 0.5, 0.1], center = true);
            rotate([0, 0, 90])
            cube([2, 0.5, 0.1], center = true);
        }
    }
}

// Main assembly
union() {
    // Document body
    paper_texture();

    // Text and decorations
    text_lines();
    corner_ornaments();

    // Ribbon with wax seal
    translate([0, 0, doc_thickness/2 + ribbon_thickness/2])
    ribbon();

    // Wax seal positioned on the ribbon
    translate([doc_width/2 - 5 - ribbon_width/2, doc_height/4 + 5, doc_thickness/2 + ribbon_thickness + seal_height/2])
    wax_seal();
}

// Roman military diploma - bronze attestation tablet (diploma militare)
// 1:2 scale model, ~90 mm tall, 3D-printable with 1 mm walls

$fn = 64;            // smooth curves
wall = 1.2;          // printable wall thickness
w = 45;              // tablet width
h = 90;              // tablet height
t = 4;               // tablet thickness
border = 3;          // raised border width
txt_h = 0.4;         // inscription depth

module tablet() {
    difference() {
        // main bronze plate
        color("bronze")
        cube([w, h, t], center=true);

        // inner recessed area
        translate([0,0,-t/2+0.1])
        cube([w-2*border, h-2*border, t], center=true);
    }
}

module border_molding() {
    // raised decorative rim
    for (i=[-1,1])
    translate([0, i*(h/2-border/2), 0])
    rotate([90,0,0])
    cylinder(h=w-border*2, r=border/2, center=true);

    for (i=[-1,1])
    translate([i*(w/2-border/2), 0, 0])
    rotate([0,90,0])
    cylinder(h=h-border*2, r=border/2, center=true);
}

module inscription_lines() {
    // horizontal ruling lines for text
    for (y=[-35:7:35])
    translate([0, y, t/2-txt_h-0.05])
    color([0.3,0.2,0.1])
    cube([w-2*border-2, 0.5, txt_h], center=true);
}

module rivet_heads() {
    // four corner rivets for original wire binding
    for (x=[-1,1])
    for (y=[-1,1])
    translate([x*(w/2-5), y*(h/2-5), t/2])
    color("bronze")
    cylinder(h=1.5, r=2, $fn=12);
}

module seal_holes() {
    // two holes for wire seal
    translate([0, h/2-10, 0])
    rotate([90,0,0])
    cylinder(h=w+2, r=1.5, center=true);

    translate([0, -h/2+10, 0])
    rotate([90,0,0])
    cylinder(h=w+2, r=1.5, center=true);
}

module imperial_motif() {
    // simplified imperial eagle at top
    translate([0, h/2-8, t/2+0.1])
    color("bronze")
    union() {
        // body
        cube([6, 4, 1], center=true);
        // wings
        for (x=[-4,4])
        translate([x,0,0])
        cube([6, 3, 1], center=true);
        // head
        translate([0, 3, 0])
        cube([3, 3, 1], center=true);
    }
}

// assembly
union() {
    tablet();
    translate([0,0,t/2+wall/2])
    border_molding();
    inscription_lines();
    rivet_heads();
    seal_holes();
    imperial_motif();
}

