// Overall dimensions: 90mm x 60mm x 20mm closed

// Main tablet dimensions
tablet_w = 90;
tablet_h = 60;
tablet_d = 8;
hinge_d = 4;
wall_t = 2;

// Wax recess dimensions
wax_w = 38;
wax_h = 50;
wax_d = 3;

// Stylus holder
stylus_r = 2;
stylus_l = 50;

// Decorative elements
boss_r = 4;
boss_h = 1;
boss_spacing = 20;

module tablet_half() {
    difference() {
        // Main tablet body
        cube([tablet_w/2, tablet_h, tablet_d]);

        // Wax recess
        translate([wall_t, wall_t, -0.1])
            cube([wax_w, wax_h, wax_d + 0.2]);

        // Stylus groove on side
        translate([tablet_w/2 - wall_t - stylus_r, 
                   tablet_h/2 - stylus_l/2, 
                   tablet_d/2])
            rotate([90, 0, 0])
            cylinder(r = stylus_r, h = stylus_l + 0.2, $fn = 16);
    }

    // Decorative corner bosses
    for (x = [wall_t + boss_r, tablet_w/2 - wall_t - boss_r]) {
        for (y = [wall_t + boss_r, tablet_h - wall_t - boss_r]) {
            translate([x, y, tablet_d])
                cylinder(r = boss_r, h = boss_h, $fn = 16);
        }
    }

    // Central decorative boss
    translate([tablet_w/4, tablet_h/2, tablet_d])
        cylinder(r = boss_r * 1.2, h = boss_h, $fn = 16);
}

module hinge() {
    // Hinge pin
    color("goldenrod")
    translate([0, 0, tablet_d/2])
        cylinder(r = hinge_d/2, h = tablet_h, $fn = 16);

    // Hinge loops on left half
    for (y = [hinge_d, tablet_h - hinge_d]) {
        translate([tablet_w/2, y, tablet_d/2])
            rotate([90, 0, 0])
            difference() {
                cylinder(r = hinge_d + wall_t, h = hinge_d, $fn = 16);
                cylinder(r = hinge_d/2 + 0.1, h = hinge_d + 0.2, $fn = 16);
            }
    }

    // Hinge pin on right half (mirrored)
    for (y = [hinge_d, tablet_h - hinge_d]) {
        translate([tablet_w/2 - hinge_d, y, tablet_d/2])
            rotate([90, 0, 0])
            difference() {
                cylinder(r = hinge_d + wall_t, h = hinge_d, $fn = 16);
                cylinder(r = hinge_d/2 + 0.1, h = hinge_d + 0.2, $fn = 16);
            }
    }
}

module clasp() {
    // Simple loop clasp on right side
    translate([tablet_w - wall_t - 4, tablet_h/2, tablet_d/2])
        difference() {
            cube([8, 6, 4]);
            translate([4, 3, 0])
                cylinder(r = 2, h = 4, $fn = 16);
        }

    // Corresponding hook on left side
    translate([wall_t + 4, tablet_h/2, tablet_d/2])
        difference() {
            cube([8, 6, 4]);
            translate([4, 3, 0])
                cylinder(r = 1.5, h = 4, $fn = 16);
        }
}

// Assembly
