// Libellus - Roman wax writing tablet
// A small, hinged wooden notebook with wax-filled recesses for writing with stylus

// Overall dimensions
length = 80;
width = 55;
thickness = 8;
hinge_diameter = 3;
wax_depth = 2;
wall_thickness = 3;

// Colors
wood_color = [139/255, 90/255, 43/255];
wax_color = [218/255, 165/255, 32/255];
metal_color = [180/255, 180/255, 180/255];

// Create the complete libellus
module libellus() {
    // Left tablet
    translate([-length/2 - 2, 0, 0]) {
        tablet_half();
    }

    // Right tablet
    translate([length/2 + 2, 0, 0]) {
        mirror([1, 0, 0]) {
            tablet_half();
        }
    }

    // Hinge pins
    color(metal_color) {
        for(i = [-1, 1]) {
            translate([0, width/4 * i, 0]) {
                cylinder(h = thickness + 2, d = hinge_diameter, $fn = 12);
            }
        }
    }
}

// Single tablet half
module tablet_half() {
    // Wooden frame
    color(wood_color) {
        difference() {
            // Outer frame
            cube([length/2, width, thickness], center = true);

            // Wax recess
            translate([0, 0, -thickness/2 + wall_thickness/2]) {
                cube([length/2 - wall_thickness*2, width - wall_thickness*2, wall_thickness*2], center = false);
            }

            // Hinge holes
            for(i = [-1, 1]) {
                translate([length/2 - wall_thickness, width/4 * i, 0]) {
                    rotate([0, 90, 0]) {
                        cylinder(h = wall_thickness*2, d = hinge_diameter + 0.5, $fn = 12);
                    }
                }
            }
        }

        // Decorative border ridge
        for(side = [0, 1]) {
            translate([0, width/2 - 2 - side*(width - 4), thickness/2 - 1]) {
                cube([length/2 - 4, 1, 1], center = true);
            }
        }
    }

    // Wax surface
    color(wax_color) {
        translate([0, 0, -thickness/2 + wall_thickness/2 + 0.1]) {
            cube([length/2 - wall_thickness*2 - 1, width - wall_thickness*2 - 1, wax_depth], center = true);
        }
    }

    // Decorative corner motifs
    color(wax_color) {
        for(x = [1, -1]) {
            for(y = [1, -1]) {
                translate([x*(length/4 - 5), y*(width/2 - 5), thickness/2 - 1]) {
                    cylinder(h = 1, d = 6, $fn = 6);
                }
            }
        }
    }

    // Stylus groove on side
    color(wood_color) {
        translate([length/4, width/2, 0]) {
            rotate([90, 0, 0]) {
                cylinder(h = width/3, d = 4, $fn = 16);
            }
        }
    }
}

// Render the libellus
libellus();

// Opus Tessellatum Roman Mosaic Panel
// 2nd Century AD Roman floor mosaic fragment
// Dimensions: 80mm x 60mm x 8mm (printable size)

// Main panel base with appropriate thickness for printing
difference() {
    color([0.9, 0.8, 0.7])  // Light mortar color
