}

// Border tesserae (small square tiles)
module border_tesserae() {
    color([0.6, 0.4, 0.2])  // Dark stone
    for(x = [0:2:79]) {
        for(y = [0:2:59]) {
            if(x <= 6 || x >= 72 || y <= 6 || y >= 52) {
                translate([x, y, 6])
                    cube([1.8, 1.8, 2]);
            }
        }
    }
}

// Central geometric pattern - Solomon's knot
module solomon_knot() {
    color([0.8, 0.1, 0.1])  // Red terracotta
    // Horizontal bars
    for(i = [-1:1]) {
        translate([40 + i*12, 30, 6])
            cube([10, 4, 2]);
    }

    // Vertical bars
    for(i = [-1:1]) {
        translate([40, 30 + i*12, 6])
            cube([4, 10, 2]);
    }

    // Intersection details
    translate([40, 30, 7])
        cube([8, 8, 1]);
}

// Corner decorative rosettes
module corner_rosette(x_pos, y_pos) {
