    // Center circle
    translate([x_pos, y_pos, 6.5])
        cylinder(h=1.5, r=3, $fn=16);

    // Petals
    for(angle = [0:45:315]) {
        rotate([0, 0, angle])
            translate([x_pos + 4, y_pos, 6.5])
                cylinder(h=1.5, r=1.5, $fn=8);
    }
}

// Inner field tesserae (background pattern)
module background_tesserae() {
    color([0.7, 0.65, 0.6])  // Light limestone

    // Diagonal pattern fill
    for(x = [8:3:71]) {
        for(y = [8:3:50]) {
            if((x + y) % 6 == 0) {
                translate([x, y, 6])
                    cube([2.5, 2.5, 2]);
            }
        }
    }
}

// Assemble the mosaic
union() {
    border_tesserae();
    background_tesserae();
    solomon_knot();

    // Add corner decorations
    corner_rosette(15, 15);
    corner_rosette(65, 15);
    corner_rosette(15, 45);
    corner_rosette(65, 45);

    // Age/wear details
    color([0.5, 0.5, 0.5])  // Weathering
    translate([25, 25, 7.5])
        sphere(r=2, $fn=16);
    translate([55, 35, 7.5])
        sphere(r=1.5, $fn=16);
}

// Tractatus Historical Artifact - Medieval Manuscript
// Realistic Style, Medium Complexity

// Main parameters
book_width = 60;
book_height = 80;
book_depth = 15;
cover_thickness = 2;
page_thickness = 11;
spine_radius = 3;

// Colors
cover_color = "SaddleBrown";
page_color = "AntiqueWhite";
metal_color = "Goldenrod";
ink_color = "Black";

module book_cover() {
    // Main cover panel
    color(cover_color)
    difference() {
        cube([book_width, book_height, cover_thickness]);

        // Inner recess for pages
        translate([cover_thickness, cover_thickness, -0.1])
        cube([book_width - 2*cover_thickness, book_height - 2*cover_thickness, cover_thickness + 0.2]);
    }

    // Ornamental corners
    for(x = [cover_thickness/2, book_width - cover_thickness/2]) {
        for(y = [cover_thickness/2, book_height - cover_thickness/2]) {
            translate([x, y, cover_thickness - 0.5])
            color(metal_color)
            cylinder(h=1, r=3, $fn=16);
        }
    }

    // Central boss
    translate([book_width/2, book_height/2, cover_thickness - 1])
    color(metal_color)
    cylinder(h=1, r=8, $fn=32);

    // Spine ridges
    for(i = [1:3]) {
        translate([0, i * book_height/4, 0])
        color(cover_color)
        cube([cover_thickness, 2, cover_thickness]);
    }
}

module pages() {
    // Page block
    color(page_color)
    translate([cover_thickness, cover_thickness, cover_thickness])
    cube([page_thickness, book_height - 2*cover_thickness, book_depth - 2*cover_thickness]);

    // Page edges texture
    for(i = [0:20]) {
        translate([cover_thickness + page_thickness * i/20, cover_thickness, cover_thickness])
        color(page_color)
        cube([page_thickness/20, book_height - 2*cover_thickness, book_depth - 2*cover_thickness]);
    }
}

module spine() {
    // Curved spine
    color(cover_color)
    translate([0, 0, 0])
    rotate([0, 90, 0])
    cylinder(h=book_height, r=spine_radius, $fn=20);

    // Spine bands
    for(i = [1:3]) {
        translate([-0.5, i * book_height/4, spine_radius])
        rotate([0, 90, 0])
        color("DarkBrown")
        cylinder(h=book_height/8, r=spine_radius + 1, $fn=20);
    }
}

module clasp() {
    // Metal clasp on front cover
    translate([book_width - 5, book_height/2, cover_thickness + 1])
    color(metal_color)
    union() {
        cube([8, 4, 1]);
        translate([8, 2, 0])
        cylinder(h=1, r=2, $fn=16);
    }

    // Corresponding loop on back cover
    translate([5, book_height/2, book_depth - cover_thickness - 1])
    color(metal_color)
    difference() {
        cube([6, 6, 1]);
        translate([3, 3, -0.1])
        cylinder(h=1.2, r=2, $fn=16);
    }
}

module inscription() {
    // Title text on spine
    translate([-1, book_height/2, book_depth/2])
    rotate([0, 90, 90])
    color(ink_color)
    linear_extrude(height=0.5)
    text("TRACTATUS", size=5, font="Times:style=Bold");

    // Decorative initial on front
    translate([book_width/2 - 5, book_height - 15, cover_thickness + 0.1])
    rotate([0, 0, 0])
    color(ink_color)
    linear_extrude(height=0.3)
    text("T", size=12, font="Times:style=Bold");
}

module bookmark() {
    // Silk bookmark ribbon
    translate([cover_thickness + page_thickness/2, cover_thickness, book_depth - cover_thickness - 2])
    color("DarkRed")
    cube([0.5, book_height - 4*cover_thickness, 2]);
}

// Assembly
union() {
    // Front cover
    book_cover();

    // Pages
    pages();

    // Spine
    spine();

    // Back cover (mirrored front)
    translate([0, 0, book_depth])
    mirror([0, 0, 1])
    book_cover();

    // Clasp assembly
    clasp();

    // Inscriptions
    inscription();

    // Bookmark
    bookmark();
}

