// Quaternion formula components
formula_height = 8;
formula_thickness = 1.5;

// Create the stone bridge base
module bridge_stone() {
    color([0.5, 0.5, 0.5]) // Weathered stone color
    difference() {
        // Main bridge stone
        cube([bridge_width, bridge_depth, bridge_height], center=true);

        // Carve arch underneath (simplified)
        translate([0, 0, -bridge_height/2 + 5])
            cylinder(h=bridge_depth, r1=bridge_width/3, r2=bridge_width/3, center=true);
    }
}

// Create the commemorative plaque
module commemorative_plaque() {
    color([0.3, 0.3, 0.3]) // Dark stone/metal color
    difference() {
        // Main plaque body
        cube([plaque_width, plaque_thickness, plaque_height], center=true);

        // Engraved border
        translate([0, 0, -plaque_thickness/2 + 0.5])
            cube([plaque_width - 4, plaque_thickness + 0.1, plaque_height - 4], center=true);
    }
}

// Create the quaternion formula i = j = k = ijk = -1
module quaternion_formula() {
    color([0.8, 0.8, 0.8]) // Engraved text color

    // i
    translate([-plaque_width/4, 0, -formula_height/2])
        cube([formula_thickness, formula_thickness, formula_height]);

    translate([-plaque_width/4, 0, formula_height/2])
        cube([formula_thickness, formula_thickness, formula_height]);

    // =
    translate([-plaque_width/8, 0, -formula_height/4])
        cube([formula_thickness*2, formula_thickness, formula_thickness]);
    translate([-plaque_width/8, 0, formula_height/4])
        cube([formula_thickness*2, formula_thickness, formula_thickness]);

    // j
    translate([0, 0, -formula_height/2])
        cube([formula_thickness, formula_thickness, formula_height]);

    translate([0, 0, formula_height/2])
        cube([formula_thickness, formula_thickness, formula_height]);

    // =
    translate([plaque_width/8, 0, -formula_height/4])
        cube([formula_thickness*2, formula_thickness, formula_thickness]);
    translate([plaque_width/8, 0, formula_height/4])
        cube([formula_thickness*2, formula_thickness, formula_thickness]);

    // k
    translate([plaque_width/4, 0, -formula_height/2])
        cube([formula_thickness, formula_thickness, formula_height]);

    translate([plaque_width/4, 0, formula_height/2])
        cube([formula_thickness, formula_thickness, formula_height]);

    // = ijk = -1
    translate([plaque_width/3, 0, 0])
        cube([formula_thickness*3, formula_thickness, formula_thickness]);
}

// Create decorative Celtic knot border
module celtic_border() {
    color([0.7, 0.7, 0.7])
    for(i = [-1, 1]) {
        for(j = [-1, 1]) {
            translate([i * (plaque_width/2 - 3), j * (plaque_height/2 - 3), 0])
                rotate([90, 0, 45])
                    cylinder(h=plaque_thickness, r1=2, r2=2);
        }
    }
}

// Create the date inscription (1843)
module date_inscription() {
    color([0.6, 0.6, 0.6])
    translate([0, -plaque_height/4, 0])
    for(i = [0:3]) {
        translate([i * 5 - 7.5, 0, 0])
            cube([2, formula_thickness, 8]);
    }
}

