// Quire - Medieval manuscript gathering of parchment sheets
// Historical characteristics: 4 folded sheets = 8 leaves = 16 pages
// Includes leather binding strips and wax tablet marking system

// Overall dimensions (mm)
book_width = 70;
book_height = 50;
book_thickness = 8;

// Page characteristics
page_thickness = 0.4;
page_count = 16;
fold_margin = 2;

// Cover materials
leather_thickness = 0.8;
strap_width = 5;

// Wax tablet details
tablet_width = 10;
tablet_height = 8;
tablet_thickness = 2;

// Parchment color
color_parchment = [0.95, 0.93, 0.88];
color_leather = [0.4, 0.2, 0.1];
color_wax = [0.6, 0.4, 0.3];

module parchment_sheet(index) {
    translate([0, 0, -book_thickness/2 + index * page_thickness * 2 + fold_margin]) {
        // Single folded sheet (two leaves)
        difference() {
            // Outer sheet
            cube([book_width, book_height, page_thickness], center=true);

            // Fold line groove
            translate([0, 0, page_thickness/2])
            cube([book_width * 0.8, 0.5, page_thickness + 0.1], center=true);
        }
    }
}

module leather_reinforcement() {
    color(color_leather) {
        // Spine strip
        translate([-book_width/2 - leather_thickness/2, 0, 0])
        cube([leather_thickness, book_height, book_thickness + 2], center=true);

        // Corner reinforcements
        for(x=[-1, 1])
        for(y=[-1, 1]) {
            translate([x * book_width/2 * 0.9, y * book_height/2 * 0.9, book_thickness/2 + leather_thickness/2])
            cube([book_width * 0.15, book_height * 0.15, leather_thickness], center=true);
        }

        // Binding straps
        for(i=[-0.3, 0, 0.3]) {
            translate([book_width/2 + leather_thickness/2, i * book_height/2, 0])
            rotate([0, 90, 0])
            cylinder(h=book_thickness + 2, r=strap_width/2, center=true);
        }
    }
}

module wax_marking_tablets() {
    color(color_wax) {
        for(side=[-1, 1]) {
            translate([side * (book_width/2 - tablet_width/2 - 2), 
                      book_height/2 - tablet_height/2 - 2, 
                      book_thickness/2 + tablet_thickness/2 + 0.5]) {
                // Main wax tablet
                cube([tablet_width, tablet_height, tablet_thickness], center=false);

                // Stylus hole
                translate([tablet_width/2, tablet_height/2, tablet_thickness/2])
                rotate([90, 0, 0])
                cylinder(h=tablet_thickness + 0.2, r=1, center=true);
            }
        }
    }
}

module stitching_holes() {
    // Medieval quire stitching pattern
    for(i=[-0.4, -0.2, 0, 0.2, 0.4]) {
        for(z=[-book_thickness/2 + 1, book_thickness/2 - 1]) {
            translate([book_width/2 + 0.1, i * book_height/2, z])
            rotate([90, 0, 0])
            cylinder(h=0.5, r=0.3, center=true);
        }
    }
}

// Main assembly
union() {
    // Parchment pages
    color(color_parchment)
    for(i=[0:page_count/4-1]) {
        parchment_sheet(i);
    }

    // Leather reinforcements
    leather_reinforcement();

    // Wax marking tablets
    wax_marking_tablets();

    // Stitching guide holes (for assembly reference)
    %stitching_holes();
}

// Bronze Age ceremonial gathering bowl - Minoan style (circa 1500 BCE)
// Used for ritual gatherings and libation ceremonies

$fn = 32;

// Main dimensions
bowl_diameter = 60;
bowl_height = 25;
wall_thickness = 2;

// Color definitions
