gold_color = [0.83, 0.69, 0.22];
dark_bronze = [0.55, 0.35, 0.15];

module ceremonial_bowl() {
    // Main bowl body
    difference() {
        // Outer shell
        cylinder(h = bowl_height, d = bowl_diameter, center = false);

        // Inner cavity
        translate([0, 0, wall_thickness])
            cylinder(h = bowl_height - wall_thickness, d = bowl_diameter - 2*wall_thickness, center = false);
    }

    // Thick rim with decorative bevel
    difference() {
        translate([0, 0, bowl_height - 3])
            cylinder(h = 5, d = bowl_diameter + 4, center = false);

        // Inner bevel
        translate([0, 0, bowl_height])
            cylinder(h = 3, d1 = bowl_diameter, d2 = bowl_diameter + 2, center = false);
    }

    // Triple handles - Minoan spiral design
    for (i = [0:120:359]) {
        rotate([0, 0, i])
            translate([bowl_diameter/2 - 2, 0, bowl_height/2])
                spiral_handle();
    }

    // Decorative band around middle with octopus motif (simplified)
    translate([0, 0, bowl_height/2])
        octopus_band();

    // Base pedestal
    translate([0, 0, -5])
        cylinder(h = 7, d1 = bowl_diameter/2, d2 = bowl_diameter/3, center = false);
}

module spiral_handle() {
    // Main handle loop
    rotate([90, 0, 0])
        translate([0, 0, -8])
            difference() {
                // Handle body
                rotate_extrude(angle = 180, $fn = 16)
                    translate([8, 0, 0])
                        circle(r = 1.5);

                // Attachment points
                translate([-10, -2, -10])
                    cube([20, 4, 20]);
            }

    // Decorative spiral ends
    translate([0, 0, -1])
        sphere(r = 2);
}

module octopus_band() {
    // Simplified octopus tentacle pattern
    for (angle = [0:30:359]) {
        rotate([0, 0, angle])
            translate([bowl_diameter/2 - 4, 0, 0])
                tentacle();
    }
}

module tentacle() {
    // Curved tentacle segment
    for (i = [0:5:20]) {
        translate([i/4, sin(i*9)*2, 0])
            scale([0.8, 0.8, 0.5])
                sphere(r = 1.2);
    }
}

// Assembly
color(bronze_color) ceremonial_bowl();

// Interior gold wash (ritual significance)
translate([0, 0, wall_thickness - 0.1])
    color(gold_color)
        cylinder(h = 0.2, d = bowl_diameter - 2*wall_thickness - 0.5, center = false);

