// Babylonian Clay Tablet with Cuneiform Inscriptions
// Typical tablet size: 50-80mm wide, 40-60mm tall, 15-20mm thick

tablet_width = 70;
tablet_height = 55;
tablet_thickness = 18;
corner_radius = 4;
cuneiform_rows = 5;
cuneiform_cols = 8;

// Main tablet body
union() {
    // Base tablet shape with rounded edges
    difference() {
        minkowski() {
            cube([tablet_width, tablet_height, tablet_thickness], center = true);
            sphere(r = corner_radius);
        }
        translate([0, 0, tablet_thickness/2 + 0.5])
            cube([tablet_width + 2, tablet_height + 2, tablet_thickness + 1], center = true);
    }

    // Cuneiform impressions on front face
    color("#8B4513") {
        for(row = [0:cuneiform_rows-1]) {
            for(col = [0:cuneiform_cols-1]) {
                translate([
                    -tablet_width/2 + 10 + col * 7,
                    -tablet_height/2 + 8 + row * 10,
                    tablet_thickness/2 - 1
                ])
                    rotate([0, 0, 45 * ((row + col) % 2)])
                        cuneiform_wedge();
            }
        }
    }

    // Seal impression on back
    translate([0, 0, -tablet_thickness/2 + 1])
        rotate([0, 0, 0])
            cylinder(h = 2, r1 = 8, r2 = 8, $fn = 16);

    // Cylinder seal pattern around central seal
    for(i = [0:11]) {
        translate([0, 0, -tablet_thickness/2 + 1])
            rotate([0, 0, i * 30])
                translate([6, 0, 0])
                    cylinder(h = 2, r1 = 1, r2 = 1);
    }

    // Edges showing clay texture marks
    color("#A0522D") {
        // Top edge finger impressions
        for(i = [0:6]) {
            translate([
                -tablet_width/2 + 10 + i * 10,
                tablet_height/2 - 2,
                0
            ])
                sphere(r = 2);
        }

        // Side edge smoothing marks
        for(i = [0:4]) {
            translate([
                tablet_width/2 - 2,
                -tablet_height/2 + 10 + i * 10,
                0
            ])
                rotate([0, 90, 0])
                    cylinder(h = 3, r1 = 1, r2 = 1);
        }
    }
}

// Module for cuneiform wedge impressions
module cuneiform_wedge() {
    difference() {
        cube([3, 1.5, 1.5], center = true);
        translate([0, 0, 0.5])
            rotate([0, 45, 0])
                cube([4, 2, 2], center = true);
    }
}

// Medieval Illuminated Manuscript Page - Realistic Style
// Based on 13th century Gothic script manuscripts

// Overall dimensions
page_width = 80;
page_height = 60;
page_thickness = 2;

// Colors
parchment_color = "#f5e6d3";
ink_color = "#2f1b14";
gold_color = "#d4af37";
red_color = "#8b0000";

// Main page module
module manuscript_page() {
    // Base parchment
    color(parchment_color) {
        difference() {
            // Main page body
            translate([0, 0, -page_thickness/2])
                cube([page_width, page_height, page_thickness], center=true);

            // Rounded corners
            translate([page_width/2-5, page_height/2-5, -page_thickness/2-0.1])
                cylinder(h=page_thickness+0.2, r=5, $fn=20);
            translate([-page_width/2+5, page_height/2-5, -page_thickness/2-0.1])
                cylinder(h=page_thickness+0.2, r=5, $fn=20);
            translate([page_width/2-5, -page_height/2+5, -page_thickness/2-0.1])
                cylinder(h=page_thickness+0.2, r=5, $fn=20);
            translate([-page_width/2+5, -page_height/2+5, -page_thickness/2-0.1])
                cylinder(h=page_thickness+0.2, r=5, $fn=20);
        }
    }

    // Decorated initial capital
    module initial_capital() {
        color(gold_color) {
            translate([-page_width/2+10, page_height/2-15, page_thickness/2])
                linear_extrude(height=0.5)
                    text("I", size=10, font="serif:style=Bold");
        }

        // Red/blue penwork decoration around initial
        color(red_color) {
            translate([-page_width/2+8, page_height/2-13, page_thickness/2+0.1])
                for(i=[0:3]) {
                    rotate([0, 0, i*90])
                        translate([0, -2, 0])
                            cube([0.5, 4, 0.3]);
                }
        }
    }

    // Gothic text lines
    module text_lines() {
        color(ink_color) {
            for(line=[0:5]) {
                translate([-page_width/2+15, page_height/2-20-line*7, page_thickness/2+0.1])
                    linear_extrude(height=0.2)
                        text("Lorem ipsum dolor sit amet consectetur", 
                             size=3, font="serif:style=Bold");
            }
        }
    }

    // Marginal decoration
    module marginalia() {
        // Left margin vine decoration
        color([0.2, 0.4, 0.1]) {
            translate([-page_width/2+3, -page_height/2+10, page_thickness/2+0.1]) {
                for(i=[0:8]) {
                    rotate([0, 0, i*20])
                        translate([0, i*3, 0])
                            cylinder(h=0.3, r=0.5);
                }
            }
        }

        // Red rubrication marks
        color(red_color) {
            translate([-page_width/2+5, page_height/2-25, page_thickness/2+0.2])
                cube([1, 20, 0.2]);
            translate([-page_width/2+5, page_height/2-46, page_thickness/2+0.2])
                cube([1, 20, 0.2]);
        }
    }

    // Illuminated border
    module border() {
        color(gold_color) {
            translate([0, page_height/2-5, page_thickness/2+0.1])
                cube([page_width-10, 0.5, 0.5]);
            translate([0, -page_height/2+5, page_thickness/2+0.1])
                cube([page_width-10, 0.5, 0.5]);
            translate([page_width/2-5, 0, page_thickness/2+0.1])
                cube([0.5, page_height-10, 0.5]);
            translate([-page_width/2+5, 0, page_thickness/2+0.1])
                cube([0.5, page_height-10, 0.5]);
        }

        // Corner decorations
        for(x=[-1:1:1]) {
            for(y=[-1:1:1]) {
                translate([x*(page_width/2-5), y*(page_height/2-5), page_thickness/2+0.2]) {
                    color(red_color) {
                        cylinder(h=0.3, r=1.5, $fn=8);
                    }
                }
            }
        }
    }

    // Wax seal impression
    module wax_seal() {
        color("#8b4513") {
            translate([page_width/2-10, -page_height/2+10, page_thickness/2+0.1]) {
                cylinder(h=1, r=4, $fn=32);
                translate([0, 0, 1])
                    cylinder(h=0.5, r=3, $fn=32);
                // Seal impression
                translate([0, 0, 1.5])
                    linear_extrude(height=0.2)
                        circle(r=2.5, $fn=6);
            }
        }
    }

    // Assemble all elements
    initial_capital();
    text_lines();
    marginalia();
    border();
    wax_seal();
}

// Generate the complete manuscript page
manuscript_page();

// Rosetta Stone Replica (196 BCE) - 3D printable historical artifact
// Realistic style, medium complexity

$fn = 50;

// Main stone dimensions (mm)
stone_width = 90;
stone_height = 70;
stone_depth = 15;

// Wall thickness for 3D printing
wall_thickness = 1.5;

module rosetta_stone() {
    // Main stone body with slight erosion
    difference() {
        // Main block
        color([0.7, 0.65, 0.55])  // Sandstone color
        cube([stone_width, stone_height, stone_depth], center=true);

        // Top erosion
        translate([0, stone_height/2 - 2, stone_depth/2 - 1])
            cube([stone_width, 4, 2], center=true);
    }

    // Rounded edges
    translate([stone_width/2 - 2, 0, 0])
        rotate([0, 0, 90])
        cylinder(h=stone_height, r=2, center=true);
    translate([-stone_width/2 + 2, 0, 0])
        rotate([0, 0, 90])
        cylinder(h=stone_height, r=2, center=true);
}

module hieroglyph_block(x, y, size) {
    // Simplified hieroglyphics pattern
    translate([x, y, stone_depth/2 + 0.1]) {
        for(i=[0:3]) {
            for(j=[0:2]) {
                translate([i*size/4 - size/2, j*size/4 - size/2, 0])
                    color([0.1, 0.1, 0.1])
                    cube([size/8, size/8, 0.2], center=true);
            }
        }
    }
}

module demotic_text(x, y, width, height) {
    // Demotic script section
    translate([x, y, stone_depth/2 + 0.1]) {
        for(i=[0:5]) {
            rotate([0, 0, 45])
            translate([i*2 - width/2, 0, 0])
                color([0.05, 0.05, 0.05])
                cube([1, height, 0.2], center=true);
        }
    }
}

module greek_text(x, y, width, height) {
    // Greek text section
    translate([x, y, stone_depth/2 + 0.1]) {
        for(i=[0:8]) {
            translate([i*width/9 - width/2 + width/18, 0, 0])
                color([0.05, 0.05, 0.05])
                cube([width/12, height, 0.2], center=true);
        }
    }
}

module inscription_lines() {
    // Top section - Hieroglyphics
    hieroglyph_block(-15, 25, 30, 20);

    // Middle section - Demotic
    demotic_text(0, 0, 80, 15);

    // Bottom section - Greek
    greek_text(0, -20, 80, 10);
}

module weathering() {
    // Surface weathering details
    for(i=[0:10]) {
        rotate([0, 0, i*36])
        translate([cos(i*36)*20, sin(i*36)*15, stone_depth/2])
            color([0.6, 0.55, 0.45])
            sphere(r=1.5);
    }
}

module mounting_holes() {
    // 3D printing friendly mounting holes
    translate([stone_width/2 - 10, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=20, r=2, center=true);
    translate([-stone_width/2 + 10, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=20, r=2, center=true);
}

// Main assembly
